/**
 * Custom styling for inline modals
 */

.k-small-inline-modal-holder {
  position: relative;
  max-width: 480px !important;
  width: 100%;
  margin: auto;
}

.k-inline-modal {
  padding: $spacing;
  background: white;
  border-radius: 5px;
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  width: 100%;
  box-sizing: border-box;
  max-width: 1080px !important;
  max-height: 800px !important;
  overflow: auto;

  form {
    margin: 0;
  }
}

.k-inline-modal__title {
  margin: 0 0 $spacing 0;
  font-weight: $regular;
  font-size: $font-size-large;
  line-height: $line-height-large;
}

/**
 * 1. Reset default <ul> styling
 */

.k-navigation {
  @include reset($padding: false, $background: false); /* 1 */
  @include border(bottom, $navigation-border);
  padding: ($navigation-spacing * 2) 0;
  background: $navigation-background;


  /* Override for rtl ul-ol-li styling */

  @include rtl {
    padding: ($navigation-spacing * 2) 0;
  }


  /* Items */

  li {
    display: block;
    line-height: $line-height;
  }


  /**
   * Links
   *
   * 1. Make item fully clickable
   * 2. removing underlines
   */

  a {
    display: block; /* 1 */
    padding-top: $navigation-spacing;
    padding-bottom: $navigation-spacing;
    padding-right: round($navigation-spacing * 2);
    padding-left: round($navigation-spacing * 3);
    font-size: $navigation-font-size;
    color: $navigation-color;
    text-decoration: none; // 2

    @include rtl {
      padding-right: round($navigation-spacing * 3);
      padding-left: round($navigation-spacing * 2);
    }


    /* Hover & focus state */

    &:hover,
    &:focus {
      background: $list-hover-background;
      color: $list-hover-color;
      text-decoration: none; /* 2 */
    }


    /**
     * Faux border
     *
     * Adding a border to the left, using "after" psuedo since "before" is used for the icon
     */

    @include psuedo(after) {
      left: 0;
      top: 0;
      width: $navigation-border-width;
      height: 100%;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* Hover & focus states */

    &:hover,
    &:focus {
      background: $navigation-hover-background;

      &:after {
        background: $navigation-hover-border;
      }
    }
  }


  /* Active link */

  .k-is-active {
    a {
      background: $navigation-active-background;
      color: $navigation-active-color;
      font-weight: $bold;

      &:after {
        top: 12%;
        height: 76%;
        background: $navigation-active-border;
      }

      &:hover {
        background: transparent;
        cursor: default;
      }
    }
  }
}

/**
 * Check if :checked is supported using modernizr
 */

@include modernizr('.k-checked') {


  /* Block element to clear from other floating / inline-block elements */

  .k-optionlist {
    @include clearfix;
    display: block;
    max-width: 100%;
    overflow: auto;
  }


  /* "Inline" table to make element as wide as children */

  .k-optionlist__content {
    position: relative;
    display: table;
    margin: 0;
    border: 1px solid $input-border;
    padding: 1px;
    border-radius: $input-border-radius;
    background: white;

    @include flexbox(0,0,true) {
      display: flex;
      flex-wrap: wrap;
      float: left;
    }


    /* Hiding the input but keeping accessible */

    input[type=radio] {
      @extend .k-visually-hidden;
    }


    /**
     * Label styling
     *
     * 1. Display as table cell so the labels line up next to each other
     * 2. Take one pixel off the radius to look smooth
     * 3. Don't let the user select
     */

    label {
      position: relative;
      z-index: 1;
      display: table-cell; /* 1 */
      margin: 0;
      padding: floor(($input-height-base - $font-size - 6px) / 2) 10px;
      line-height: 1.25em;
      text-align: center;
      color: $input-color;
      font-weight: normal;
      border-radius: $input-border-radius - 1px; /* 2 */
      cursor: pointer;
      user-select: none; /* 3 */
      word-wrap: break-word;
      overflow-wrap: break-word;
      @include transition;

      @include flexbox(0,0,true) {
        flex: 1 1 auto;
      }


      /**
       * Fake background to be able to zoom in in from the center on select
       *
       * 1. Cover the entire parent
       * 2. Low z-index so label content will stay on top
       * 3. Hide the element by default
       * 4. Same radius as parent
       * 5. Scale to 0,0
       */

      &:before {
        @include coverall; /* 1 */
        z-index: -1; /* 2 */
        display: block;
        content: " ";
        opacity: 0; /* 3 */
        border-radius: $input-border-radius - 1px; /* 4 */
        transform: scale(0,0); /* 5 */
        background: $color-neutral;
        @include transition;
      }


      /* A little darker background on hovering (no need for focus since this is a `<label>`) */

      &:hover {
        background: rgba(0, 0, 0, 0.15);
      }
    }


    /**
     * Styling label when checked
     *
     * 1. Transparent background for label itself
     * 2. Show psuedo element
     */

    input:checked + label {
      &, &:hover, &:focus {
        background: transparent; /* 1 */
        color: $color-neutral-type;
      }

      &:before {
        transform: scale(1,1); /* 2 */
        opacity: 1;; /* 2 */
      }
    }


    /**
     * Fake focus
     *
     * We need to create a separate element for the fake focus
     * The children have position: relative so we can't just fill up the entire parent div
     *
     * 1. Position it on top of the parent element but behind the labels
     * 2. Hide by default
     * 3. Display on focus
     * 4. Set border-radius same as parent
     */

    .k-optionlist__focus {
      @include coverall(-1px); /* 1 */
      z-index: 0; /* 1 */
      outline: 0;
      display: block;
      opacity: 0; /* 2 */
      visibility: hidden; /* 2 */
      border-radius: $input-border-radius; /* 4 */
      border: 1px solid $input-border-focus;
      box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba($input-border-focus, 0.6);
    }


    /* Display on focus and active */

    input:active ~ .k-optionlist__focus,
     input:focus ~ .k-optionlist__focus {
       opacity: 1; /* 3 */
       visibility: visible; /* 3 */
     }
  }
}

@include modernizr-rtl('.k-checked') {
  .k-optionlist__content {
    @include flexbox(0, 0, true) {
      float: right;
    }
  }
}

/**
 * Check if :checked is supported using modernizr
 */

@include modernizr('.k-checked') {
  .k-optionlist--boolean {
    input[value="1"] + label {
      &:before {
        background: $radio-toggle-1; /* 1 */
      }
    }

    input[value="0"] + label {
      &:before {
        background: $radio-toggle-0; /* 1 */
      }
    }
  }
}

@include modernizr('.k-checked') {

  /* Block element to clear from other floating / inline-block elements */

  .k-optionlist--block {
    .k-optionlist__content {
      width: 100%;

      @include flexbox(0,0,true) {
        float: none;
      }
    }

    &.k-optionlist--boolean {
      label {
        width: 50%;
      }
    }
  }
}

@include modernizr('.k-checked') {

  /* Block element to clear from other floating / inline-block elements */

  .k-optionlist {
    [disabled] + label {
      cursor: $cursor-disabled;
      opacity: 0.6;

      &:hover {
        background: transparent;
      }

      &:active ~ .k-optionlist__focus {
        border-color: $color-error;
      }
    }
  }
}

/**
 * 1. Faking a border for when items are not stretching
 */

.k-scopebar {
  @include clearfix;
  background: $scopebar-background;
  color: $scopebar-color;
  min-width: 100%;
  box-shadow: inset 0 -1px 0 $scopebar-border; /* 1 */


  /* Faking flexbox */

  @include breakpoint($delta) {
    display: table;
    width: 100%;
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    display: flex;
    width: auto;
    flex: 0 0 auto;
    flex-wrap: wrap;
  }

  @include flexbox($delta, 0) {
    flex-wrap: nowrap;
  }
}


/**
 * Scopebar items
 *
 * 1. For wrapping text in combination with flexbox
 */

.k-scopebar__item {
  @include clearfix;
  @include border(bottom, $scopebar-border);
  min-width: 0; /* 1 */
  box-shadow: 0 -1px 0 $scopebar-border;


  /* Floating nicely */

  @include breakpoint($alpha) {
    float: left;
  }


  /* Faking flexbox */

  @include breakpoint($delta) {
    display: table-cell;
    width: 100%;
    float: none;
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    flex: 1 1 auto;
    width: auto;
  }
}


/**
 * Static scopebar items
 */

.k-scopebar__item--static {


  /* Faking flexbox */

  @include breakpoint($delta) {
    width: 1%;
    white-space: nowrap;
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    display: block;
    flex: 0 0 auto;
  }
}



/**
 * Toggle buttons
 *
 * 1. Clearing for floating buttons
 * 2. Move first button away form edge
 */

.k-scopebar__item--toggle-buttons {
  @include clearfix; /* 1 */
  padding-left: 4px; /* 2 */

  @include breakpoint($alpha) {
    float: left;
  }

  @include breakpoint($beta) {
    float: right;
  }


  /* Hide on larger screens */

  @include breakpoint($delta) {
    display: none;
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    flex: 1 1 auto;
    order: 15;
    width: auto;
  }

  @include flexbox($beta, 0) {
    width: auto;
    flex: 0 0 auto;
  }


  /* RTL */

  @include rtl {
    padding-left: 0;
    padding-right: 4px;
  }


  /* Buttons */

  .k-toggle-scopebar-filters,
  .k-toggle-scopebar-breadcrumbs {
    display: block;
    float: left;

    @include breakpoint($beta) {
      display: none;
    }
  }

  .k-toggle-scopebar-search {
    display: block;
    float: right;

    @include breakpoint($delta) {
      display: none;
    }
  }
}

.k-scopebar--breadcrumbs {
  .k-scopebar__item--toggle-buttons {
    @include flexbox(0, 0) {
      flex: 0 0 auto;
    }
  }
}

.k-scopebar__item--filters {


  /* Hide when JS is enabled */

  @include js-enabled {
    display: none;


    /* Show on larger screens */

    @include breakpoint($alpha) {
      width: 100%;
    }

    @include breakpoint($beta) {
      width: auto;
      display: block !important;
    }


    /* Faking flexbox */

    @include breakpoint($delta) {
      display: table-cell !important;
      width: 100%;
    }
  }


  /**
   * Flexbox
   *
   * 1. Reset order
   */

  @include flexbox(0, 0) {
    width: 100%;
    flex: 1 1 auto;
    order: 10;
  }

  @include flexbox($beta, 0) {
    width: auto;
    order: 0; /* 1 */
  }

  @include flexbox($delta, 0) {
    display: block !important;
  }
}


/**
 * 1. Clearfix so we can float individual filters
 * 2. For positioning dropdowns on smaller screens
 */

.k-scopebar__filters {
  @include clearfix; /* 1 */
  position: relative; /* 2 */
  z-index: 2; /* 2 */
}


/* Individual filter */

.k-scopebar__item--filter {
  display: block;
  float: left;
  width: auto;
  @include border(right, $color-primary-min-2);
  @include border(bottom, $color-primary-min-2);
  margin-bottom: -1px;

  @include breakpoint($beta) {
    position: relative;
  }

  @include rtl {
    border-right: none;
    @include border(left, #ccc);
    float: right;
  }
}


/* Filter title and icon */

.k-scopebar__item--filter__title,
.k-scopebar__item--filter__icon {
  display: inline-block;
  vertical-align: middle;
}

.k-scopebar__item--filter__icon {
  margin-left: 2px;

  @include rtl {
    margin-left: 0;
    margin-right: 2px;
  }

  @include breakpoint($beta) {
    margin-left: 4px;

    @include rtl {
      margin-left: 0;
      margin-right: 4px;
    }
  }
}


/* Scopebar label showing how many active filters you have below a filter button */

.k-scopebar__item-label {
  position: absolute;
  z-index: 2;
  top: 2px;
  right: 2px;
  min-width: $scopebar-filter-label-size;
  height: $scopebar-filter-label-size;
  border-radius: 50%;
  background: $scopebar-filter-label-background;
  color: $scopebar-filter-label-color;
  font-size: $scopebar-filter-label-font-size;
  line-height: $scopebar-filter-label-size;
  text-align: center;
  vertical-align: middle;
  font-weight: $bold;
  box-shadow: inset 0 0 2px rgba(0, 0, 0, 0.1);
  pointer-events: none;

  @include rtl {
    right: auto;
    left: 2px;
  }

  @include breakpoint($beta) {
    top: 6px;
    right: 4px;

    @include rtl {
      right: auto;
      left: 4px;
    }
  }

  &--numberless {
    &:after {
      display: block;
      content: " ";
      width: 5px;
      height: 5px;
      background: white;
      position: absolute;
      top: 5px;
      left: 5px;
      border-radius: 50%;
      box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
    }
  }
}

.k-scopebar-dropdown {
  overflow: hidden;

  @include breakpoint($beta) {
    position: relative;
    z-index: 2;
  }

  &.k-is-active {
    overflow: visible;
    z-index: 3;

    .k-scopebar-dropdown__body {
      visibility: visible;
    }

    .k-scopebar__item--filter__title {
      text-decoration: underline;
    }
  }

  &.k-is-active {
    .k-scopebar-dropdown__button {
      background: $scopebar-buttons-hover-background;
    }
  }
}

.k-scopebar-dropdown__button {
  position: relative;
  border: none;
  background: transparent;
  margin: 0;
  padding: 0 6px;
  line-height: $scopebar-size;
  height: $scopebar-size;
  font-size: $scopebar-filter-font-size;

  &:hover,
  &:focus {
    background: $scopebar-buttons-hover-background;
  }

  @include breakpoint($beta) {
    padding: 0 12px;
  }
}

.k-scopebar-dropdown__body {
  position: absolute;
  visibility: hidden;
  background: $dropdown-background;
  @include border(all, $dropdown-border);
  color: $dropdown-color;
  box-shadow: 2px 2px 3px rgba(0, 0, 0, 0.15);
  border-radius: 0 0 5px 5px;
  min-width: 200px;
  left: 0;
  right: 0;
  top: 100%;

  @include breakpoint($beta) {
    min-width: 300px;
    right: auto;
    left: -1px;

    @include rtl {
      left: auto;
      right: -1px;
    }
  }

  .select2 {
    min-width: 100%;
  }
}

.k-scopebar-dropdown__body__content {
  padding: 5px;
}

.k-scopebar-dropdown__body__buttons {
  line-height: 14px;
  @include border(top, $dropdown-buttons-border);
  background: $dropdown-buttons-background;
  color: $dropdown-buttons-color;
  padding: 8px 5px;
  text-align: center;
  border-radius: 0 0 5px 5px;

  .btn {
    padding: 4px 30px;
    line-height: 16px;
  }
}

.k-scopebar__item--breadcrumbs {


  /* Hide when JS is enabled */

  @include js-enabled {
    display: none;


    /* Show on larger screens */

    @include breakpoint($alpha) {
      width: 100%;
    }


    /* Show on larger screens */

    @include breakpoint($beta) {
      display: block !important;
    }


    /* Faking flexbox */

    @include breakpoint($delta) {
      display: table-cell !important;
      overflow: auto;
      max-width: 0;
    }
  }


  /**
   * Flexbox
   *
   * 1. Reset order
   */

  @include flexbox(0, 0) {
    width: auto;
    flex: 1 1 auto;
    order: -1;
    display: block;
  }

  @include flexbox($beta, 0) {
    width: auto;
    order: 0; /* 1 */
  }

  @include flexbox($delta, 0) {
    display: flex !important;
    flex: 1 1 auto;
    max-width: 100%;
    overflow: hidden;
  }


  /* Adjusted styling to display nicely inside the scopebar */

  .k-breadcrumb {
    position: static;
    top: auto;
    bottom: auto;
    visibility: visible;
    border-bottom: none;
    overflow: auto;
    text-overflow: ellipsis;
    white-space: nowrap;

    @include flexbox(0, 0) {
      flex: 1 1 auto;
    }
  }
}

.k-scopebar__item--buttons {


  /* Floating nicely */

  @include breakpoint($alpha) {
    float: right;
  }


  /* Stop floating on larger screens for faking flexbox */

  @include breakpoint($delta) {
    float: none;
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    display: block;
    flex: 0 0 auto;
  }
}

.k-scopebar__button {
  @include reset;
  position: relative;
  display: block;
  float: left;
  height: round($scopebar-size * 0.8);
  margin: round($scopebar-size * 0.1);
  margin-left: 0;
  padding-left: round((($scopebar-size * 0.8) - (2px + 16px)) / 2);
  padding-right: round((($scopebar-size * 0.8) - (2px + 16px)) / 2);
  border-radius: 2px;
  text-align: center;
  line-height: round($scopebar-size * 0.8);
  cursor: pointer;
  @include transition;
  @include border(all, transparent);

  @include rtl {
    float: right;
    margin-left: round($scopebar-size * 0.1);
    margin-right: 0;
  }

  &:hover {
    border-color: $scopebar-border;
    background: lighter($scopebar-background);
  }

  &.k-is-active {
    border-color: $scopebar-border;
    background: lighter($scopebar-background);
  }


  /**
   * 1. Absolute center
   */

  [class^="k-icon"] {
    display: block;
    margin-top: round((($scopebar-size * 0.8) - (2px + 16px)) / 2); /* 1 */
    margin-bottom: round((($scopebar-size * 0.8) - (2px + 16px)) / 2); /* 1 */
    width: 16px;
    height: 16px;
    line-height: 0;
    font-size: 0;
    vertical-align: middle;

    &:before {
      font-size: $icon-size;
      line-height: $icon-size;
    }
  }
}

.k-scopebar__item--search {
  position: relative;


  /* Hide when JS is enabled */

  @include js-enabled {
    display: none;
    width: 100%;


    /* Show on larger screens and faking flexbox */

    @include breakpoint($delta) {
      display: table-cell !important;
      float: none;
    }
  }


  /* Flexbox */

  @include flexbox(0, 0) {
    flex: 1 1 auto;
    order: 20;
  }


  /**
   * 1. Reset order
   * 2. Set proper widths
   */

  @include flexbox($delta, 0) {
    display: block !important;
    flex: 0 0 auto;
    order: 0; /* 1 */
  }

  @include breakpoint($delta) {
    @include allwidth($scopebar-search-size-delta); /* 2 */
  }

  @include breakpoint($echo) {
    @include allwidth($scopebar-search-size-echo); /* 2 */
  }

  .k-search {
    width: 100%;
    position: relative;
  }

  .k-search--has-search-button .k-search__field {
    padding-left: $scopebar-submit-button-spacing;

    @include rtl {
      padding-left: 0;
      padding-right: $scopebar-submit-button-spacing;
    }
  }

  .k-search--has-empty-button .k-search__field {
    padding-right: $scopebar-empty-button-spacing;

    @include rtl {
      padding-right: 0;
      padding-left: $scopebar-empty-button-spacing;
    }
  }

  .k-search--has-both-buttons .k-search__field {
    padding-left: $scopebar-submit-button-spacing;
    padding-right: $scopebar-empty-button-spacing;

    @include rtl {
      padding-left: $scopebar-empty-button-spacing;
      padding-right: $scopebar-submit-button-spacing;
    }
  }


  /* Label */

  label {
    @extend .k-visually-hidden;
  }


  /* The search field */

  .k-search__field {
    @include reset;
    margin: 0;
    display: block;
    width: 100%;
    height: $scopebar-size;
    border-radius: 0;
    padding: $spacing-tiny;
    padding-right: $scopebar-size;
    background: $scopebar-search-background;
    color: $scopebar-search-color;
    box-shadow: none;
    -webkit-appearance: none;

    @include rtl {
      padding-right: 0;
      padding-left: $scopebar-size;
    }

    @include breakpoint($delta) {
      @include border(left, $scopebar-border);
      @include border(bottom, $scopebar-border);
      height: $scopebar-size + 1px;
      margin-bottom: -1px;

      @include rtl {
        border-left: none;
        @include border(right, $scopebar-border);
      }
    }
  }


  /* Search buttons */

  .k-search__submit,
  .k-search__empty {
    @include reset;
    position: absolute;
    top: 50%;
    width: round($scopebar-size / 2);
    height: round($scopebar-size / 2);
    margin-top: -(round($scopebar-size / 4));
    vertical-align: middle;
    line-height: 1;
    cursor: pointer;
    color: $color-primary-min-5;

    [class*="k-icon-"] {
      vertical-align: middle;
    }

    &:focus,
    &:hover {
      color: $color-primary-min-7;
    }

    &.k-is-visible {
      display: inline-block;
    }
  }


  /* The search submit button */

  .k-search__submit {
    left: (($scopebar-submit-button-spacing - round($scopebar-size / 2)) / 2);
    font-size: $scopebar-submit-button-size;

    @include rtl {
      left: auto;
      right: (($scopebar-submit-button-spacing - round($scopebar-size / 2)) / 2);
    }
  }


  /* The search empty / reset button */

  .k-search__empty {
    display: none;
    right: (($scopebar-empty-button-spacing - round($scopebar-size / 2)) / 2);
    font-size: $scopebar-empty-button-size;

    @include rtl {
      right: auto;
      left: (($scopebar-empty-button-spacing - round($scopebar-size / 2)) / 2);
    }

    &.k-is-visible {
      display: inline-block;
    }
  }


  /*  The active search label */

  .k-scopebar__item-label--numberless {
    position: absolute;
    right: auto;
    left: 13px;
    top: 5px;

    @include rtl {
      left: auto;
      right: 13px;
    }
  }
}

/**
 * 100% wide by default instead of just 220px making it responsive
 * Important because width is being added by JS as inline styling
 * But only inside koowa container
 */


.koowa .select2.select2-container--bootstrap {
  width: 100% !important;
  max-width: 100% !important;
}

.select2-container--bootstrap {
  max-width: 100% !important;

  /* Set proper line-height so text is vertically centered */

  .select2-selection--single {
    padding: $spacing-base-vertical $spacing-base-horizontal;

    .select2-selection__rendered {
      line-height: $line-height;
    }
  }


  /* Set proper height so arrow is vertically centered */

  .select2-selection--single {
    .select2-selection__arrow {
      height: $input-height-base;
    }
  }


  .select2-selection--multiple {
    .select2-selection__rendered {
      padding-right: 5px;
    }

    .select2-selection__choice {
      max-width: 300px;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }


  /* Set proper styling for the search input field */

  .select2-search__field {
    height: $input-height-base;
    margin: 0;
  }

  .select2-search--inline {
    &:first-child {
      .select2-search__field {
        width: auto !important;
      }
    }

    .select2-search__field {
      height: $input-height-base - 2px;
      margin: 0;
      box-shadow: none;
      background: transparent;
      border: none;
    }
  }


  /**
   * 1. Set font-size to 16px on touch supported devices so browser won't automatically zoom
   */

  [tabindex],
  input {
    @include modernizr('.k-touchevents') {
      &:focus,
      &:active {
        font-size: 16px !important; /* 1 */
      }
    }
  }
}

.k-sidebar-item {
  & + & {
    margin-top: $spacing;
  }


  /**
   * 1. Should be the same as in `.k-sidebar-[left/right]`
   */

  @include flexbox($sidebar-left-querie-width, 599px) { /* 1 */
    flex: 0 0 auto;

    &--flex {
      display: flex;
      flex-direction: column;
      flex: 0 1 auto;
      overflow: hidden;
      min-height: 100px;

      div {
        flex: 1 1 auto;
        overflow: auto;
      }

      .k-sidebar-item__header {
        flex: 0 0 auto;
      }
    }
  }


  /**
   * Overflowing items
   *
   * 1. Set container to overflow it's items
   */

  &--overflow {
    overflow: auto; /* 1 */
    width: 100%;

    @include flexbox($vertical: 550px) {
      flex: 0 1 auto;
    }

    @include resizing {
      overflow: hidden;
    }
  }
}


/**
 * Toggle item
 *
 * 1. Same as width but minus 2px border width
 */

.k-sidebar-item--toggle {
  padding-bottom: $spacing-small;

  .k-sidebar-item__header {
    position: relative;
  }

  .k-sidebar-item__toggle {
    position: absolute;
    top: $spacing;
    right: $spacing;

    @include icon-after(chevron-top, true) {
      display: block;
      background: white;
      border: 1px solid #ddd;
      border-radius: 10px;
      width: 20px;
      line-height: 18px; /* 1 */
      text-align: center;
      vertical-align: middle;
      font-size: $font-size-tiny;
      cursor: pointer;
      @include transition;
    }

    &.k-is-active:after {
      transform: rotate(180deg);
    }

    @include rtl {
      right: auto;
      left: $spacing;
    }
  }
}

.k-sidebar-item__header {
  padding: $spacing $spacing 0;
  font-weight: $bold;
  text-transform: uppercase;
  font-size: $font-size-small;
  line-height: $line-height-small;
}

.k-sidebar-item__content {
  padding: $spacing;
}

.k-sidebar-item__content--small {
  padding: $spacing-small $spacing;
}

.k-sidebar-item__content--horizontal {
  padding: 0 $spacing;
}

.k-table-container {
  background: $table-background;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

.k-table {
  overflow: auto;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    background: $table-cell-background-even;
  }
}


/**
 * Default table settings
 *
 * 1. override browser default
 */

.k-table-container {
  table {
    width: 100%;
    text-align: left;
    border-collapse: separate;
    border-spacing: 0;
    font-size: inherit; /* 1 */
    min-width: 0 !important;

    @include rtl {
      text-align: right;
    }

    @include flexbox {
      flex: 1 1 auto;
    }
  }


  /**
   * Cells
   *
   * 1. Position checkbox
   * 2. Remove default margin
   */

  th,
  td {
    border: none;
    position: relative;
    height: $table-cell-size;
    vertical-align: middle;
    text-align: left;
    line-height: 1;
    @include transition;

    input {
      display: inline-block; /* 1 */
      vertical-align: middle; /* 1 */
      margin: 0; /* 2 */
    }
  }

  @include rtl {
    th,
    td {
      text-align: right;
    }
  }


  /**
   * Head
   *
   * 1. Don't set a vertical padding on table headers. Set padding on table heading children
   *    We do this so <a> elements are fully clickable for sorting
   * 2. Don't use long header titles since they won't be wrapped
   * 3. Whole <th> should be clickable
   * 4. Set padding here
   */

  th {
    @include border(bottom, $table-header-border);
    z-index: 1;
    padding: 0 $table-cell-spacing-horizontal; /* 1 */
    white-space: nowrap; /* 2 */
    background: $table-header-background;
    font-weight: $regular;

    a {
      display: block; /* 3 */
      padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal; /* 4 */
      margin-left: -$table-cell-spacing-horizontal;
      margin-right: -$table-cell-spacing-horizontal;
      line-height: $table-cell-size - ($table-cell-spacing-vertical * 2);
      text-decoration: none;
      @include transition;

      &:focus,
      &:hover {
        background: $table-header-hover-background;
      }
    }


    /* Size icons accordingly */

    [class*="k-icon"] {
      font-size: $icon-size;
      line-height: 1;
      vertical-align: middle;
    }
  }


  /**
   * Body
   *
   * 1. Small extra info below title display:block to display below title
   */

  td {
    @include border(bottom, $table-cell-border);
    padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal;
    background: $table-cell-background-even;
  }

  tr:last-child {
    td {
      border-bottom: none;
    }
  }


  tr:nth-child(odd) {
    td {
      background: $table-cell-background-odd;
    }
  }


  /* Hover background */

  tr,
  tr:nth-child(n) {
    &:focus,
    &:hover {
      td {
        background: $table-cell-hover-background-even;
      }
    }
  }

  [class*="k-icon"] {
    display: inline-block;
  }
}

@import "pagination";


/**
 * Table elements
 */

/**
 * 1. Faking a bottom border, this shadow will disappear when pagination is positioned to bottom
 */

.k-table-pagination {
  padding: $table-pagination-spacing;
  background: $table-pagination-background;
  @include border(top, $table-pagination-border);
  @if $borders != false {
    box-shadow: 0 1px 0 $table-pagination-border; /* 1 */
  }

  /* Flexbox */

  @include flexbox {
    flex: 0 0 auto;
  }

}

.k-pagination {

  /**
   * Pagination contents
   *
   * 1. Align vertically
   */

  .k-pagination__limit,
  .k-pagination__pages {
    display: inline-block; /* 1 */
    vertical-align: middle; /* 1 */
  }

  /**
   * Pagination
   *
   * 1. Remove list-style and block-level defaults
   * 2. Collapse white-space
   */

  .k-pagination__pages {
    padding-left: 0;
    margin: 0;
    border-radius: $border-radius;


    /* Override for rtl ul-ol-li styling */

    @include rtl {
      padding: 0;
    }


    /* Contents */

    & > li {
      display: inline; /* 1 */

      & > a,
      & > span {
        display: block;
        position: relative;
        float: left; /* 2 */
        padding: $spacing-base-vertical $spacing-base-horizontal;
        line-height: $line-height;
        text-decoration: none;
        color: $pagination-color;
        background-color: $pagination-background;
        border: 1px solid $pagination-border;
        margin-left: -1px;
      }

      @include rtl {
        & > a,
        & > span {
          float: right;
          margin-left: 0;
          margin-right: -1px;
        }
      }

      &:first-child {
        & > a,
        & > span {
          margin-left: 0;
          @include border-left-radius($border-radius);
        }

        @include rtl {
          & > a,
          & > span {
            margin-right: 0;
            @include border-left-radius(0);
            @include border-right-radius($border-radius);
          }
        }
      }

      &:last-child {
        & > a,
        & > span {
          @include border-right-radius($border-radius);

          @include rtl {
            @include border-right-radius(0);
            @include border-left-radius($border-radius);
          }
        }
      }
    }

    & > li > a,
    & > li > span {
      &:hover,
      &:focus {
        z-index: 2;
        color: $pagination-hover-color;
        background-color: $pagination-hover-background;
        border-color: $pagination-hover-border;
      }
    }

    & > .k-is-active > a,
    & > .k-is-active > span {
      &,
      &:hover,
      &:focus {
        z-index: 3;
        color: $pagination-active-color;
        background-color: $pagination-active-background;
        border-color: $pagination-active-border;
        cursor: default;
      }
    }
  }

  /* Limit */

  select {
    height: $table-pagination-height;
    width: auto;
    margin: 0;
    margin-right: $table-pagination-spacing;

    @include rtl {
      margin-right: auto;
      margin-left: $table-pagination-spacing;
    }
  }
}

.k-table-container {
  a {
    display: inline-block;
    vertical-align: middle;
    text-decoration: none;
    @include transition;

    &:hover,
    &:focus {
      text-decoration: underline;
    }
  }

  small {
    display: block;
    font-size: $font-size-small;
    line-height: $line-height-small;
  }
}

.k-table-container {
  .k-table-data--right {
    text-align: right;

    @include rtl {
      text-align: left;
    }
  }

  .k-table-data--center {
    text-align: center;
  }

  .k-table-data--nowrap {
    white-space: nowrap;
  }

  .k-table-data--wrap {
    word-break: break-all;
  }

  .k-table-data--toggle,
  .k-table-data--form,
  .k-table-data--icon {
    white-space: nowrap;
    line-height: 1;
    vertical-align: middle;

    a, span, input, svg, div, small, img {
      vertical-align: middle;
      line-height: 1;
    }

    [class*="k-icon"] {
      font-size: $icon-size;
    }
  }

  .k-table-data--multiline {
    a, span, input, svg, small, img {
      display: inline;
      vertical-align: baseline;
    }
  }

  .k-table-data--toggle {
    padding: 0;
    font-size: 0;
    line-height: 0;
    overflow: hidden;
    width: 0;
    visibility: visible;
  }

  .k-table-data--toggle--hidden {
    padding: 0;

    span, div, img, a {
      display: none !important;
    }
  }

  .footable.breakpoint {
    .k-table-data--toggle {
      width: 1%;
      padding-left: $table-cell-spacing-horizontal;
      padding-right: $table-cell-spacing-horizontal;
    }
  }

  .k-table-data--form {
    input {
      margin: 0 !important;
    }
  }

  .k-table-data--icon {
    img {
      @include allwidth(16px);
    }
  }

  th.k-table-data--icon {
    a {
      line-height: $table-cell-size - ($table-cell-spacing-vertical * 2);
    }
  }

  .k-table-data--ellipsis {
    max-width: 0;
    overflow: hidden;

    a, span, input, svg, div, small {
      vertical-align: middle;
      max-width: 100%;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }
}

.k-table-container {
  .k-table__item--state {
    @include transition;


    /* pointers for clickable elements */

    button#{&},
    a#{&} {
      cursor: pointer;

      &:focus,
      &:hover {
        text-decoration: underline;
      }
    }

    &-pending {
      color: $color-warning;
    }

    &-expired {
      color: $color-neutral;
    }

    &-unpublished {
      color: $color-error;
    }

    &-published {
      color: $color-success;
    }
  }
}

.k-table-container {
  .k-table__sub-header {
    th {
      padding: 0.7em $table-cell-spacing-horizontal 0.5em;
      height: auto;
      font-size: $font-size-small;
      line-height: $line-height-small;
      font-weight: $bold;
      vertical-align: middle;
      text-transform: uppercase;
      white-space: nowrap;
      background: $table-header-background;
      border-bottom-color: $table-cell-border;
    }
  }
}

.k-table-container {
  .k-table__item-level {
    white-space: nowrap;

    &:before {
      content: " ";
      display: block;
      width: 6px;
      height: 6px;
      border-left: 1px solid #bbb;
      border-bottom: 1px solid #bbb;
      position: absolute;
      top: 50%;
      margin-top: -.25em;

      @include rtl {
        border-left: none;
        border-right: 1px solid #bbb;
      }
    }
  }


  /* Set proper spacing before levels */

  @for $i from 1 through 15 {
    .k-table__item-level#{$i} {
      padding-left: 24px * ($i - 1) + $table-cell-spacing-horizontal;

      @include rtl {
        padding-left: 0;
        padding-right: 24px * ($i - 1) + $table-cell-spacing-horizontal;
      }

      &:before {
        left: (24px * ($i - 1)) - (18px - $table-cell-spacing-horizontal);

        @include rtl {
          left: auto;
          right: (24px * ($i - 1)) - (18px - $table-cell-spacing-horizontal);
        }
      }
    }
  }


  /* Do not display the level icon for the first level items */

  .k-table__item-level1:before {
    display: none;
  }


  /* Space icons away from text */

  .k-table__item-level__icon-item {
    position: relative;
    padding-left: $icon-size + $spacing-tiny;

    @include rtl {
      padding-left: 0;
      padding-right: $icon-size + $spacing-tiny;
    }

    [class*="k-icon"],
    [class*="k-image"] {
      position: absolute;
      left: 0;
    }

    @include rtl {
      [class*="k-icon"],
      [class*="k-image"] {
        left: auto;
        right: 0;
      }
    }

    [class*="k-icon"] {
      top: 50%;
      font-size: $icon-size;
      margin-top: -(round($icon-size / 2));
    }

    [class*="k-image"] {
      height: $icon-size;
      line-height: $icon-size;
      vertical-align: middle;
    }
  }
}


/**
 * Selected
 *
 * When rows are selected they are highlighted
 *
 * 1. Coloring Links and extra elements that don't color automatically with their parent
 */

.k-table-container {
  tr.k-is-selected {
    td {
      background: $table-cell-accent-background;
      color: $table-cell-accent-color;

      a, i, span {
        color: $table-cell-accent-color;
      }
    }


    /* Re-set background on td's while hovering th */

    &:hover,
    &:focus {
      td {
        background-color: $table-cell-accent-hover-background;
      }
    }
  }
}

.k-positioner {
  display: inline-block;
  vertical-align: middle;
  width: $table-row-positioner-size;
  height: round($table-row-positioner-size / 4);
  margin-top: round(($table-row-positioner-size - (round($table-row-positioner-size / 4))) / 2);
  margin-bottom: round(($table-row-positioner-size - (round($table-row-positioner-size / 4))) / 2);
  border-top: 1px solid $table-row-positioner-color;
  border-bottom: 1px solid $table-row-positioner-color;
  cursor: no-drop;

  &.k-is-active {
    cursor: move;
  }

  @include psuedo(before) {
    width: $table-row-positioner-size;
    height: round(($table-row-positioner-size / 2) + 2px);
    border-top: 1px solid $table-row-positioner-color;
    border-bottom: 1px solid $table-row-positioner-color;
    top: -(round($table-row-positioner-size / 4));
  }

  &:after {
    position: absolute;
    width: ($table-row-positioner-size * 2);
    height: ($table-row-positioner-size * 2);
    top: -($table-row-positioner-size - 1px);
    left: -($table-row-positioner-size / 2);
    content: attr(data-position);
    text-indent: 3px;
    vertical-align: bottom;
    color: #333;
    text-shadow: 0 0 3px white;
    @include transition;
  }
}

.k-table-container {
  tr.k-is-selected {
    .k-positioner {
      &:after {
        color: white;
        text-shadow: none;
      }
    }
  }
}

.k-sort-ascending,
.k-sort-descending {
  position: relative;
  display: inline-block;
  width: 6px;
  height: 3px;
  background: transparent;

  &:before,
  &:after {
    display: block;
    content: " ";
    position: absolute;
    opacity: 0.33;
    @include transition;
  }

  &:before {
    @include caret(top, 4px);
    bottom: 100%;
  }

  &:after {
    @include caret(bottom, 4px);
    top: 100%;
  }
}

.k-sort-ascending {
  &:before {
    opacity: 1;
  }
}

.k-sort-descending {
  &:after {
    opacity: 1;
  }
}

th {
  .k-sort-ascending,
  .k-sort-descending {
    margin-left: 0.66em;
    vertical-align: middle;
  }

  @include rtl {
    .k-sort-ascending,
    .k-sort-descending {
      margin-left: 0;
      margin-right: 0.66em;
    }
  }

  a:focus,
  a:hover {
    .k-sort-ascending {
      &:after {
        opacity: 0.66;
      }
    }

    .k-sort-descending {
      &:before {
        opacity: 0.66;
      }
    }
  }
}

@import "responsive-table";

.footable.breakpoint {
  .footable-toggle {
    content: " ";
    display: inline-block;
    width: $footable-toggle-size;
    height: $footable-toggle-size;
    line-height: ($footable-toggle-size - ($footable-toggle-border-size * 2));
    border: $footable-toggle-border-size solid $footable-toggle-color;
    border-radius: 50%;
    text-align: center;
    vertical-align: middle;

    @include icon-after(chevron-bottom, true) {
      display: inline-block;
      width: $footable-toggle-icon-size;
      height: $footable-toggle-icon-size;
      margin: ((($footable-toggle-size - $footable-toggle-icon-size) / 2) - $footable-toggle-border-size);
      color: $footable-toggle-color;
      font-size: $footable-toggle-icon-size;
      line-height: 1.3em;
      transform: rotate(-90deg);
      @include transition;
    }
  }

  tr.k-is-selected {
    .footable-toggle {
      border-color: $footable-toggle-color-active;

      &:after {
        color: $footable-toggle-color-active;
      }
    }
  }


  /**
   * Reset padding, and re-set them on individual divs
   */

  .footable-row-detail-cell {
    padding: 0;
  }

  tr.footable-detail-show {
    .footable-toggle {
      &:after {
        transform: rotate(0deg);
      }
    }
  }

  .footable-row-detail-row {
    @include clearfix;
    @include content-block($table-cell-spacing-vertical);
  }

  .footable-row-detail-name {
    font-weight: $bold;
  }


  /**
   * 1. Just like table cells
   * 2. Almost as tall as the main table rows
   * 3. Same spacing as on table cells
   */

  .footable-row-detail-name,
  .footable-row-detail-value {
    position: relative; /* 1 */
    min-height: round($unit-size * 0.75); /* 2 */
    padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal; /* 3 */
  }


  /* Hide toggle button for sub headings */

  th {
    .footable-toggle {
      display: none;
    }
  }
}

/**
 * This class is created by the floatThead plugin that's why it's not k-float-thead-wrapper
 *
 * 1. Overriding inline styling, we don't need a z-index of 1000+
 */

.floatThead-wrapper {
  z-index: 1 !important; /* 1 */

  @include flexbox {
    display: flex;
    flex: 0 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

.k-tabs {
  @include clearfix;
  margin-bottom: 0;
  padding-left: 0;
  list-style: none;
  border-bottom: 1px solid $nav-tabs-border;


  /**
   * 1. Make the list-items overlay the bottom border
   */

  & > li {
    position: relative;
    display: block;
    float: left;
    margin-bottom: -1px; /* 1 */

    & > a {
      position: relative;
      display: block;
      padding: $nav-tabs-link-spacing;
      margin-right: 2px;
      line-height: $line-height;
      border: 1px solid transparent;
      border-radius: $border-radius $border-radius 0 0;
      text-decoration: none;

      &:hover,
      &:focus {
        text-decoration: none;
        background-color: $nav-tabs-link-hover-background;
        border-color: $nav-tabs-link-hover-border $nav-tabs-link-hover-border $nav-tabs-border;
      }
    }


    /**
     * Disabled state sets text to gray and nukes hover/tab effects
     */

    &.k-is-disabled > a {
      color: $nav-tabs-disabled-link-color;
      cursor: $cursor-disabled;
      pointer-events: none;

      &:hover,
      &:focus {
        color: inherit;
        text-decoration: none;
        background-color: transparent;
        cursor: $cursor-disabled;
      }
    }


    /**
     * Active state, and its :hover to override normal :hover
     */

    &.k-is-active > a {
      &,
      &:hover,
      &:focus {
        color: $nav-tabs-active-link-hover-color;
        background-color: $nav-tabs-active-link-hover-background;
        border: 1px solid $nav-tabs-active-link-hover-border;
        border-bottom-color: transparent;
        cursor: default;
      }
    }
  }


  /**
   * Open dropdowns
   */

  .k-is-open > a {
    &,
    &:hover,
    &:focus {
      background-color: $nav-tabs-link-hover-background;
      border-color: $color-accent;
    }
  }
}

/**
 * Tabbable tabs
 *
 * Hide tabbable panes to start, show them when `.k-is-active`
 */

.k-tabs-content {
  & > .k-tab {
    display: none;
    padding: $spacing-small;
  }

  & > .k-is-active {
    display: block;
  }
}

/**
 * 1. Set the z-index higher than the menus
 * 2. Setting a background so container will fall nicely over the sidebar
 * 3. Set transition for off canvas sidebar
 */

.k-title-bar {
  @include border(bottom, $title-bar-border);
  position: relative; /* 1 */
  z-index: 10; /* 1 */
  display: table;
  width: 100%;
  background: $title-bar-background; /* 2 */
  color: $title-bar-color;
  font-size: $font-size-huge;
  @include transition(transform); /* 3 */


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/**
 * Titlebar when toggled
 *
 * 1. Set left to the width of the menu so the menu will appear
 * 2. Set right to the negative width of the menu so the menu will appear
 * 3. Reset when screen is wide enough
 */

.k-show-left-menu {
  & > .k-title-bar {
    transform: translate($sidebar-left-width, 0); /* 1 */

    @include rtl {
      transform: translate(-($sidebar-left-width), 0);
    }

    @include breakpoint($sidebar-left-querie-width) {
      transform: translate(0, 0); /* 3 */

      @include rtl {
        transform: translate(0, 0);
      }
    }
  }
}

.k-show-right-menu {
  & > .k-title-bar {
    transform: translate(-($sidebar-right-width), 0); /* 2 */

    @include rtl {
      transform: translate($sidebar-right-width, 0); /* 2 */
    }

    @include breakpoint($sidebar-right-querie-width) {
      transform: translate(0, 0); /* 3 */

      @include rtl {
        transform: translate(0, 0);
      }
    }
  }
}


/**
 * Fake Overlay when toggled
 *
 * 1. Set opacity
 * 2. Set z-index so it will become visible
 */

.k-show-left-menu,
.k-show-right-menu {
  .k-title-bar:before {
    opacity: 0.75; /* 1 */
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-title-bar:before {
    z-index: 2; /* 2 */
  }
}


/**
 * Titlebar contents
 *
 * 1. Also cover the bottom border
 */

.k-title-bar {
  .k-off-canvas-overlay {
    bottom: -1px; /* 1 */
  }

  .k-off-canvas-menu-toggle-holder,
  .k-title-bar__heading {
    display: table-cell;
    vertical-align: middle;
  }

  .k-off-canvas-menu-toggle-holder {
    white-space: nowrap;
  }

  .k-title-bar__heading {
    width: 100%;
    padding: $spacing-small;
    text-align: center;

    @include breakpoint($charlie) {
      text-align: left;

      @include rtl {
        text-align: right;
      }
    }
  }
}

.k-title-bar--mobile {
  @include border(bottom, $title-bar-mobileborder);
  background: $title-bar-mobilebackground;

  @include breakpoint($charlie) {
    display: none;
  }
}

.k-title-bar--inline {
  background: transparent;
  border: none;

  .k-title-bar__heading {
    padding: 0;
  }
}

.k-toolbar {
  @include border(bottom, $toolbar-border);
  background: $toolbar-background;
  min-height: $toolbar-height;
  padding-bottom: 10px;


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/**
 * Toolbar contents
 */

.k-toolbar {
  .k-button {
    margin: 10px 0 0 10px;


    /**
     * Make buttons square
     *
     * Only make them square if media queries are supported
     * By default we just want them as buttons
     */

    @include breakpoint(0, range, $charlie) {
      overflow: hidden;
      width: 32px;
      height: 32px;
      padding: 7px 6px;
      text-align: center;
    }
  }

  @include rtl {
    .k-button {
      margin: 10px 10px 0 0;
    }
  }


  /* Autosave indicator */

  .k-autosave-indicator {
    margin: 10px 0 0 10px;
    display: inline-block;
    vertical-align: middle;
    padding: 5px;

    @include rtl {
      margin: 10px 10px 0 0;
    }
  }


  /**
   * Position the icons
   *
   * Only position them if media queries are supported
   * By default we just want them aligned normally
   */

  [class^="k-icon"] {
    @include breakpoint(0, range, $charlie) {
      display: block;
      margin: 0 0 10px;
      width: $font-size-large;
      height: $font-size-large;
      font-size: $font-size-large;
      line-height: $font-size-large;
    }

    display: inline-block;
    margin: 0 0.25em 0 0.1em;

    @include rtl {
      margin: 0 0.1em 0 0.25em;
    }
  }


  .k-off-canvas-menu-toggle-holder {
    display: inline-block;
    vertical-align: middle;

    .k-off-canvas-menu-toggle {
      margin-bottom: 0;
    }
  }
}


/* Text on the toolbar buttons */

.k-toolbar__text {
  @include breakpoint(0, range, $charlie) {
    position: relative;
    z-index: -1;
    visibility: hidden;
  }
}

/**
 * 1. Our parent element can be arbitrary since tooltips are by default inserted as a sibling of their target element.
 *    So reset our font and text properties to avoid inheriting weird values.
 */

.k-tooltip {
  position: absolute;
  z-index: $zindex-tooltip;
  display: block;
  font-size: $font-size-small; /* 1 */
  opacity: 0;

  &.in {
    opacity: $tooltip-opacity;
  }

  &.top {
    margin-top: -3px;
    padding: $tooltip-arrow-width 0;
  }

  &.right {
    margin-left: 3px;
    padding: 0 $tooltip-arrow-width;
  }

  &.bottom {
    margin-top: 3px;
    padding: $tooltip-arrow-width 0;
  }

  &.left {
    margin-left: -3px;
    padding: 0 $tooltip-arrow-width;
  }
}


/**
 * Wrapper for the tooltip content
 */

.k-tooltip__inner {
  max-width: $tooltip-max-width;
  padding: 3px 8px;
  color: $tooltip-color;
  text-align: center;
  background-color: $tooltip-background;
  border-radius: $border-radius;
}


/* Arrows */

.k-tooltip__arrow {
  position: absolute;
  width: 0;
  height: 0;
  border-color: transparent;
  border-style: solid;
}

.k-tooltip {
  &.top .k-tooltip__arrow {
    bottom: 0;
    left: 50%;
    margin-left: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }

  &.top-left .k-tooltip__arrow {
    bottom: 0;
    right: $tooltip-arrow-width;
    margin-bottom: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }

  &.top-right .k-tooltip__arrow {
    bottom: 0;
    left: $tooltip-arrow-width;
    margin-bottom: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width 0;
    border-top-color: $tooltip-arrow-color;
  }

  &.right .k-tooltip__arrow {
    top: 50%;
    left: 0;
    margin-top: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width $tooltip-arrow-width $tooltip-arrow-width 0;
    border-right-color: $tooltip-arrow-color;
  }

  &.left .k-tooltip__arrow {
    top: 50%;
    right: 0;
    margin-top: -$tooltip-arrow-width;
    border-width: $tooltip-arrow-width 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-left-color: $tooltip-arrow-color;
  }

  &.bottom .k-tooltip__arrow {
    top: 0;
    left: 50%;
    margin-left: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }

  &.bottom-left .k-tooltip__arrow {
    top: 0;
    right: $tooltip-arrow-width;
    margin-top: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }

  &.bottom-right .k-tooltip__arrow {
    top: 0;
    left: $tooltip-arrow-width;
    margin-top: -$tooltip-arrow-width;
    border-width: 0 $tooltip-arrow-width $tooltip-arrow-width;
    border-bottom-color: $tooltip-arrow-color;
  }
}

.k-tree {
  @include clearfix;
  margin: $list-spacing 0;
  overflow: auto;

  .jqtree-element {
    padding: 0 $spacing-tiny 0 $spacing;

    @include rtl {
      padding: 0 $spacing 0 $spacing-tiny;
    }

    &:hover,
    &:focus {
      background: $jqtree-hover-background;
    }
  }

  & > .jqtree-tree {
    margin: 0;
    padding: 0;

    & > .jqtree_common {
      .jqtree-element {
        padding-left: 0;

        @include rtl {
          padding-left: $spacing-tiny;
          padding-right: 0;
        }
      }
    }
  }

  .jqtree-selected {
    & > .jqtree-element {
      background: $color-accent;
      color: $color-accent-type;
    }

    &:hover {
      & > .jqtree-element {
        background: dark($color-accent);
      }
    }
  }
}

.k-sidebar-item {
  .k-tree {
    & > .jqtree-tree {
      & > .jqtree-folder {
        .jqtree-element {
          padding-left: 15px;

          @include rtl {
            padding-left: $spacing-tiny;
            padding-right: 15px;
          }
        }
      }
    }
  }
}

.jqtree-tree {
  @include clearfix;


  /* Reset default ul li styling */

  &, ul {
    @include reset;
    @include clearfix;
    float: left;
    min-width: 100%;

    @include rtl {
      float: right;
    }
  }
}

.jqtree-folder {
  @include clearfix;
}

.jqtree-element {
  position: relative;
  display: block;
  width: 100%;
  font-size: 0;
  padding: 0;
  white-space: nowrap;
  height: auto;
  cursor: pointer;

  a {
    padding: 0;
    background: transparent;
  }

  .jqtree-toggler {
    height: 0;
    padding-top: $tree-size;
    overflow: hidden;
    color: inherit;
  }
}

.jqtree-whitespace,
.jqtree-toggler,
.jqtree-icon,
.jqtree-title {
  display: inline-block;
  font-size: $font-size;
  line-height: $tree-size;
  height: $tree-size;
  vertical-align: middle;
}

.jqtree-whitespace,
.jqtree-toggler,
.jqtree-icon {
  width: $tree-size;
  text-align: center;
}

.jqtree-icon {
  &:before {
    line-height: 26px;
  }
}

.k-tree {
  li li {
    .jqtree-whitespace + .jqtree_common:not(.jqtree-toggler) {
      @include psuedo(after) {
        width: 6px;
        height: 6px;
        border-left: 1px solid #aaa;
        border-bottom: 1px solid #aaa;
        top: 50%;
        margin-top: -3px;
        left: -(($tree-size / 2) + 3px);

        @include rtl {
          border-left: none;
          border-right: 1px solid #aaa;
          left: auto;
          right: -(($tree-size / 2) + 3px);
        }
      }
    }
  }
}

.jqtree-toggler {
  @include psuedo(before) {
    top: 50%;
    left: 50%;
    margin-top: -(round($tree-toggle-size / 2) - 1px);
    margin-left: -($tree-toggle-size);
    @include caret(bottom, $tree-toggle-size);
    @include transition;

    // @include rtl { works fine without RTL }
  }

  &:hover,
  &:focus {
    &:before {
      border-top-color: black;
    }
  }

  &.jqtree-closed {
    &:before {
      transform: rotate(-90deg);

      @include rtl {
        transform: rotate(90deg);
      }
    }
  }
}

.jqtree-selected {
  & > .jqtree-element {
    font-weight: $bold;
  }

  & > .jqtree_common > .jqtree-toggler {
    &:hover {
      color: $color-accent-type;

      &:before {
        border-top-color: $color-accent-type;
      }
    }
  }
}

.jqtree-closed {
  ul {
    display: none;
  }
}

.jqtree--one-level {
  .jqtree-whitespace {
    display: none;
  }
}

.k-well {
  padding: $spacing-small;
  background-color: $well-background;
  border: 1px solid $well-border;
  border-radius: $border-radius;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
  @include content-block;

  p {
    @include content-block($spacing-small);
  }
}

.k-well__content {
  padding: $spacing-small;
  background: white;
  border: 1px solid $well-border;
  border-radius: $border-radius;
}

.k-well {
  .k-tabs-content {
    margin: 0;
    background: white;
    border: 1px solid #ddd;
    border-top: none;
    border-radius: 0 0 $border-radius $border-radius;
  }
}

@import "component";
@import "content";
@import "content-wrapper";
@import "grid";
@import "flex-wrapper";
@import "koowa-container";
@import "sidebar";
@import "wrapper";

/**
 * 1. By using a max-width we essentially set a min-width when the contents are
 *    to large for the koowa container. The reason we don't use min-width is
 *    because if the content is only 300px we don't need horizontal scrollbars
 * 2. Reset the max-width when viewport is as large as minwidth
 * 3. Default styling
 */

&.koowa-container {
  max-width: $minwidth; /* 1 */
  margin: 0; /* 6 */
  border: none; /* 6 */
  width: 100%;
  overflow: hidden;
  padding: $koowa-padding;

  background: $koowa-background; /* 3 */
  color: $color-type; /* 3 */

  -webkit-font-smoothing: $webkit-font-smoothing;
  -moz-osx-font-smoothing: $firefox-font-smoothing;
  -webkit-overflow-scrolling: touch;

  @include breakpoint($minwidth) {
    max-width: none; /* 2 */
  }


  /* When flexbox is enabled */

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
  }


  /* RTL */

  @include rtl {
    direction: rtl;
  }
}

.k-wrapper {
  position: relative;


  /* When flexbox is in use */

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: hidden;
  }
}


/**
 * Wrapper when toggled
 *
 * 1. Hide overflowing when menu is toggled
 * 2. Set back to auto when screen is wide enough
 * 3. Hide all overflow when resizing to avoid jumpy layout
 */

.k-is-opened-left,
.k-is-opened-right {
  overflow: hidden; /* 1 */

  @include breakpoint($sidebar-left-querie-width) {
    overflow: auto; /* 2 */
  }

  @include breakpoint($sidebar-right-querie-width) {
    overflow: auto; /* 2 */
  }

  @include resizing {
    overflow: hidden; /* 3 */
  }
}

/**
 * 1. Clearing toolbar floats when flexbox is not in use
 */

.k-content-wrapper {
  clear: both; /* 1 */
  margin: 0;
  overflow: hidden;
  @include border(bottom, $content-border);


  /* Faking flexbox on older browsers by using table styling */

  display: table;
  width: 100%;


  /* When flexbox is in use */

  @include flexbox {
    display: flex;
    flex-direction: row;
    flex: 1 1 auto;
    border-bottom: none;
  }
}

/* The left and right sidebar */

.k-sidebar-left,
.k-sidebar-right {
  position: relative;
  overflow: auto;
  -webkit-overflow-scrolling: touch;


  /* When resizing */

  @include resizing {
    overflow: hidden;
  }


  /* Faking flexbox on older browsers by using table styling */

  display: table-cell;
  vertical-align: top;


  /* When JavaScript is enabled */

  @include js-enabled {
    position: absolute;
    z-index: -1;
    top: 0;
    height: 100%;
    display: none;
  }


  /* When flexbox is in use */

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 0 0 auto;
  }


  /**
   * No flex, but rather overflowing sidebar
   * Made for screens between 391px and 550px in height
   * Make sure that categories in the sidebar are always accessible no matter how many menu items you've got
   *
   * 1. Should be the same as in `.k-sidebar-item--flex`
   */

  @media (min-width: $sidebar-left-querie-width) and (min-height: $flexbox-vertical) {
    overflow: auto;
  }

  @media (min-width: $sidebar-left-querie-width) and (min-height: 599px) { /* 1 */
    overflow: hidden;
  }
}

.k-sidebar-left {

  /* Faking flexbox on older browsers by using table styling */

  @include breakpoint($sidebar-left-querie-width) {
    @include allwidth($sidebar-left-width-small);
  }


  /* Larger sidebar when space is available */

  @include breakpoint($delta) {
    @include allwidth($sidebar-left-width);
  }


  /* When JavaScript is enabled */

  @include js-enabled {
    @include allwidth($sidebar-left-width);


    /* On 'desktop' we 'open' the sidebar by default */

    @include breakpoint($sidebar-left-querie-width) {
      position: relative;
      display: table-cell;
      height: auto;
      @include allwidth($sidebar-left-width-small);
      z-index: 1;
    }


    /* And we make it even larger when there's enough space */

    @include breakpoint($delta) {
      @include allwidth($sidebar-left-width);
    }
  }


  /* Override js-enabled beta styling with flexbox */

  @include flexbox {
    @include breakpoint($sidebar-left-querie-width) {
      display: flex;
    }
  }
}

.k-sidebar-right {

  /* Faking flexbox on older browsers by using table styling */

  @include breakpoint($sidebar-right-querie-width) {
    @include allwidth($sidebar-right-width-small);
  }


  /* Larger sidebar when space is available */

  @include breakpoint($sidebar-right-querie-width) {
    @include allwidth($sidebar-right-width);
  }


  /* When JavaScript is enabled */

  @include js-enabled {
    @include allwidth($sidebar-right-width);


    /* On 'desktop' we 'open' the sidebar by default */

    @include breakpoint($sidebar-right-querie-width) {
      position: relative;
      display: table-cell;
      height: auto;
      @include allwidth($sidebar-right-width-small);
      z-index: 1;
    }


    /* And we make it even larger when there's enough space */

    @include breakpoint($delta) {
      @include allwidth($sidebar-right-width);
    }
  }

  /* Override js-enabled beta styling with flexbox */

  @include flexbox {
    @include breakpoint($sidebar-left-querie-width) {
      display: flex;
    }
  }
}


/* Left sidebar specific styling */

.k-sidebar-left {
  @include border(right, $sidebar-border);
  background: $sidebar-background;
  color: $sidebar-color;

  @include rtl {
    border-right: none;
    @include border(left, $sidebar-border);
  }

  @include js-enabled {
    left: 0;
  }

  @include js-enabled {
    @include rtl {
      left: auto;
      right: 0;
    }
  }
}


/* When left sidebar is toggled */

.k-is-opened-left {
  .k-sidebar-left {
    display: block;
    z-index: 1;
  }
}


/* Right sidebar specific styling */

.k-sidebar-right {
  @include border(left, $sidebar-border);
  background: $sidebar-background;
  color: $sidebar-color;

  @include rtl {
    border-left: none;
    @include border(right, $sidebar-border);
  }

  @include js-enabled {
    right: 0;
  }

  @include js-enabled {
    @include rtl {
      right: auto;
      left: 0;
    }
  }
}


/* When right sidebar is toggled */

.k-is-opened-right {
  .k-sidebar-right {
    display: block;
    z-index: 1;
  }
}

/**
 * 1. Set the z-index higher than the menus
 * 2. Setting a background so container will fall nicely over the sidebar
 * 3. Set transition for off canvas sidebar
 */

.k-content {
  position: relative; /* 1 */
  z-index: 2; /* 1 */
  background: $content-background; /* 2 */
  @include transition(transform); /* 3 */


  /* Faking flexbox on older browsers by using table styling */

  display: table-cell;
  width: 100%;


  /**
   * When flexbox is in use
   *
   * 1. Reset width needed for table
   * 2. Set overflow for breadcrumbs to properly work
   */

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    width: auto; /* 1 */
    overflow: hidden; /* 2 */
  }
}


/**
 * Container when toggled
 *
 * 1. Set left to the width of the menu so the menu will appear
 * 2. Set right to the negative width of the menu so the menu will appear
 * 3. Reset when screen is wide enough
 */

.k-show-left-menu {
  .k-content {
    transform: translate($sidebar-left-width, 0); /* 1 */

    @include breakpoint($sidebar-left-querie-width) {
      transform: translate(0, 0); /* 3 */
    }
  }

  @include rtl {
    .k-content {
      transform: translate(-($sidebar-left-width), 0);

      @include breakpoint($sidebar-left-querie-width) {
        transform: translate(0, 0);
      }
    }
  }
}

.k-show-right-menu {
  .k-content {
    transform: translate(-($sidebar-right-width), 0); /* 2 */

    @include breakpoint($sidebar-right-querie-width) {
      transform: translate(0, 0); /* 3 */
    }
  }

  @include rtl {
    .k-content {
      transform: translate($sidebar-right-width, 0);

      @include breakpoint($sidebar-right-querie-width) {
        transform: translate(0, 0);
      }
    }
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-content {
    overflow: hidden;

    @include breakpoint($sidebar-left-querie-width) {
      overflow: auto; /* 3 */
    }

    @include breakpoint($sidebar-right-querie-width) {
      overflow: auto; /* 3 */
    }

    @include resizing {
      overflow: hidden;
    }
  }
}


/**
 * The overlay
 *
 * 1. Cover the entire container
 * 2. Hide by setting z-index to -1 because item can't transition if display is set from none to block
 * 3. Set opacity to 0 by default
 * 4. Hide overlay when screen is wide enough
 */

.k-off-canvas-overlay {
  @include coverall; /* 1 */
  z-index: -1; /* 2 */
  display: block;
  opacity: 0; /* 3 */
  content: " ";
  background: $off-canvas-overlay-background;
  @include transition(opacity);
}


/**
 * Overlay when toggled
 *
 * 1. Set opacity
 * 2. Set z-index to 1 so it will become visible
 */

.k-show-left-menu,
.k-show-right-menu {
  .k-off-canvas-overlay {
    opacity: $off-canvas-overlay-opacity; /* 1 */
  }
}

.k-is-opened-left,
.k-is-opened-right {
  .k-off-canvas-overlay {
    z-index: 9; /* 2 */
  }
}

.k-component {
  background: $component-background;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

/* Container */

.k-container {
  @include clearfix;
  overflow: hidden;
  width: 100%;
  margin: 0 auto (-($spacing));
  padding: $spacing ($spacing / 2);

  /* When flexbox is in use */
  @include flexbox {
    flex: 0 0 auto;
  }

  /* When resizing */
  @include resizing {
    overflow: hidden;
  }
}


/* Flex container */

.k-container--flex {
  margin: 0 auto;
  padding: $spacing;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
  }
}


/* --------------------------------------------------------------------------
   Grid children
   -------------------------------------------------------------------------- */

/* Main container */

.k-container__main {
  margin-bottom: $spacing;

  @include breakpoint($charlie) {
    float: left;
    margin: 0 ($spacing / 2) $spacing;
    width: calc(100% * 8/12 - #{$spacing});

    @include rtl {
      float: right;
    }
  }
}


/* When a sidebar is active next to it */

.k-sidebar + .k-content .k-container__main {
  @include breakpoint($charlie) {
    width: 100%;
  }

  @include breakpoint($delta) {
    width: calc(100% * 8/12 - #{$spacing});
    margin-bottom: 0;
  }
}


/* Sub container */

.k-container__sub {
  margin-bottom: $spacing;

  @include breakpoint($charlie) {
    float: left;
    margin: 0 ($spacing / 2) $spacing;
    width: calc(100% * 4/12 - #{$spacing});

    @include rtl {
      float: right;
    }
  }
}


/* When a sidebar is active next to it */

.k-sidebar + .k-content .k-container__sub {
  @include breakpoint($charlie) {
    width: 100%;
  }

  @include breakpoint($delta) {
    width: calc(100% * 4/12 - #{$spacing});
    margin-bottom: 0;
  }
}


/* Full container */

.k-container__full {
  clear: both;
  width: auto;
  margin: 0 ($spacing / 2) $spacing;
}

.k-flex-wrapper {
  @include reset;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }
}

.k-dynamic-content-holder.k-dynamic-content-holder.k-dynamic-content-holder {
  display: none;
}

.k-is-hidden.k-is-hidden.k-is-hidden {
  display: none;
}

.k-loader {
  @include modernizr('.k-cssanimations.k-boxshadow') {
    display: inline-block;
    color: $loader-color;
    font-size: $loader-size-small;
    margin: 0 auto 2em;
    position: relative;
    text-indent: -9999em;
    vertical-align: middle;

    &,
    &:before,
    &:after {
      border-radius: 50%;
      width: 1em;
      height: 1em;
      animation-fill-mode: both;
      animation: k-loader $loader-duration infinite ease-in-out;
    }

    & {
      left: 1.25em;
    }

    &:before {
      left: -1.25em;
      animation-delay: -($loader-duration / 8);
    }

    &:after {
      left: 1.25em;
      animation-delay: ($loader-duration / 8);
    }

    &:before,
    &:after {
      content: '';
      position: absolute;
      top: 0;
    }


    /* Sizes */

    &.k-loader--small {
      font-size: $loader-size-small;
    }

    &.k-loader--medium {
      font-size: $loader-size-medium;
    }

    &.k-loader--large {
      font-size: $loader-size-large;
    }
  }

  @keyframes k-loader {
    0%,
    80%,
    100% {
      box-shadow: 0 1em 0 -0.5em;
    }
    40% {
      box-shadow: 0 1em 0 0;
    }
  }

  @include rtl {
    @include flip-item;
  }
}

.k-loader-container {
  @include coverall;
  z-index: 5;
  background: rgba($loader-container-background,$loader-container-opacity);
  display: flex;
  align-items: center;

  .k-loader {
    @include modernizr('.k-cssanimations.k-boxshadow') {
      left: 0;
    }
  }
}

.k-no-margin.k-no-margin.k-no-margin {
  margin: 0;
}

.k-no-transition.k-no-transition.k-no-transition {
  transition: none;
}

.k-no-wrap.k-no-wrap.k-no-wrap {
  white-space: nowrap;
}

.k-visually-hidden.k-visually-hidden.k-visually-hidden {
  position: absolute;
  overflow: hidden;
  clip: rect(0 0 0 0);
  height: 1px;
  width: 1px;
  margin: -1px;
  padding: 0;
  border: 0;
}

.k-visually-shown.k-visually-shown.k-visually-shown {
  position: static;
  overflow: visible;
  clip: auto;
  height: auto;
  width: auto;
  margin: 0;
}

.k-ratio-block {
  position: relative;
  width: 100%;

  &--16-to-9 {
    @include ratio-block(16 to 9);
  }

  &--4-to-3 {
    @include ratio-block(4 to 3);
  }

  &--3-to-2 {
    @include ratio-block(3 to 2);
  }

  &--2-to-3 {
    @include ratio-block(2 to 3);
  }

  &--2-to-1 {
    @include ratio-block(2 to 1);
  }

  &--1-to-2 {
    @include ratio-block(1 to 2);
  }

  &--1-to-1 {
    @include ratio-block(1 to 1);
  }

  &.k-is-loaded {
    .k-loader.k-loader {
      opacity: 0;
    }
  }

  .k-loader.k-loader {
    position: absolute;
    top: 50%;
    left: 50%;
    z-index: 1;
    transform: translate(-50%, -50%);
  }

  .k-ratio-block__body {
    @include coverall;
    z-index: 2;
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .k-ratio-block__centered {
    display: block;
    flex: 0 0 auto;
    overflow: auto;
    max-width: 100%;
    max-height: 100%;
    backface-visibility: hidden;

    img {
      max-width: 100%;
      max-height: 100%;
    }
  }
}


/*
Colors

markup:
<div class="k-color-error">...</div>

*/

.k-color-error {
  color: $color-error;
}


/*
Content block

Element with a bottom margin except when item itself is a last-child

markup:
<div class="k-content-block">...</div>

*/

.k-content-block {
  @include content-block;
}

.k-content-block--small {
  @include content-block(1em);
}


/*
Relative positioned element

Could be useful to add to parnet sof absolutely positioned items

markup:
<div class="k-no-transition">...</div>

*/

.k-position-relative {
  position: relative;
}


/*
Flexing boxes

DO or don't flexing children. USeful inside flexing containers on elemtns that have no pre-defined styling for flexbox

markup:
<div class="k-dont-flex">...</div>
<div class="k-do-flex">...</div>

*/

.k-dont-flex.k-dont-flex.k-dont-flex {
  @include flexbox {
    flex: 0 0 auto;
  }
}

.k-do-flex.k-do-flex.k-do-flex {
  @include flexbox {
    flex: 1 1 auto;
  }
}


/*
Hide elements for touch enabled devices

markup:
<div class="k-no-transition">...</div>

*/

.k-touchevents-hidden {
  @include modernizr('.k-touchevents') {
    display: none !important;
  }
}


/*
Text overflow

markup:
<div class="k-ellipsis">
  <div class="k-ellipsis__item"></div>
</div>

*/

.k-ellipsis {
  max-width: 100%;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;

  a, span, input, svg, div, small {
    display: inline-block;
    vertical-align: middle;
    max-width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
}

.k-ellipsis__item {
  // Item to be ellipsed
}


/*
"Cover all" link

Use a psuedo element inside the `<a>` to vover the entire parent.
- This way we don't have to make a the parent element a link.
- Downside: You can't select the text anymore, but this could also be used to your advantage

markup:
<div class="k-link-coverall">...</div>

*/

.k-link-coverall {
  @include psuedo(after, false) {
    @include coverall;
  }
}


/*
"On-top" links

Use inside the same parent as the above "coverall" link to place this link on top of the psuedo element

markup:
<div class="k-link-ontop">...</div>

*/

.k-link-ontop {
  position: relative;
  z-index: 1;
}


/*
This or that

Line items up next to each other

markup:
<div class="k-this-or-that">...</div>

*/

.k-this-or-that {
  display: inline-block;
  margin-right: $spacing-tiny;
  vertical-align: middle;

  @include rtl {
    margin-right: 0;
    margin-left: $spacing-tiny;
  }
}

section.css:
@import "a";
@import "dl";
@import "fieldset";
@import "img";
@import "input";
@import "label";
@import "legend";
@import "p";
@import "ul-ol-li";

a {
  color: $link-color;

  &:focus,
  &:hover {
    color: $link-color;
  }
}

dl {
  @include clearfix;
  @include content-block;
}

dt {
  @include reset;
  font-weight: $bold;
  float: left;
  clear: left;
  margin-right: $spacing-tiny;
}

dd {
  @include reset;
  display: block;
  @include content-block($spacing-tiny);
  max-width: 100%;

  &.k-ellipsis {
    vertical-align: bottom;

    .k-ellipsis__item {
      vertical-align: inherit;
    }
  }
}

/**
 * 1. Chrome and Firefox set a `min-width: min-content;` on fieldsets,
 *    so we reset that to ensure it behaves more like a standard block element.
 *    See https://github.com/twbs/bootstrap/issues/12359.
 */

fieldset {
  @include reset;
  @include content-block;
  min-width: 0; /* 1 */
}

/**
 * 1. Responsive images
 * 2. Eliminating line-height bug when used alone in container
 * 3. No hidden borders on parents
 */

img {
  max-width: 100%; /* 1 */
  height: auto;

  &:only-child {
    vertical-align: middle; /* 2 */
    backface-visibility: hidden; /* 3 */
  }
}


.k-image-16 {
  @include allwidth(16px);
}

/**
 * 1. Force IE8 to wrap long content
 */

label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  margin-bottom: $spacing-tiny;
  font-weight: $bold;

  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * 1. No margin top for the first legend
 * 2. No margin bottom for the last legend
 */

legend {
  width: 100%;
  padding: 0;
  color: $color-type;
  border: 0;
  display: block;
  margin: 0;
  border: none;
  border-bottom: 1px solid darker($component-background);
  padding-top: $spacing;
  padding-bottom: $spacing-tiny;
  font-size: $font-size-huge;
  line-height: $line-height-huge;
  font-weight: $regular;
  @include content-block;
}

/**
 * 1. Remove default top-margin
 */

p {
  margin-top: 0; /* 1 */
  @include content-block;
}

ul, ol {
  @include content-block;
  margin-left: 0;
  margin-right: 0;
  padding-left: 1.5em;
}

@include rtl {
  ul, ol {
    padding-left: 0;
    padding-right: 1.5em;
  }
}

@import "alerts";
@import "breadcrumb";
@import "buttons";
@import "card";
@import "empty-state";
@import "form-block";
@import "forms";
@import "gallery";
@import "heading";
@import "icons";
@import "list";
@import "menubar";
@import "modal";
@import "navigation";
@import "optionlist";
@import "scopebar";
@import "sidebar-item";
@import "table";
@import "tabs";
@import "title-bar";
@import "toolbar";
@import "tooltip";
@import "tree";
@import "wells";

.k-alert {
  padding: $alert-spacing;
  @include content-block;

  a {
    color: inherit;
  }
}


/* Overflowing alert with 33% max-height */

.k-alert--overflow {
  min-height: 75px;
  max-height: 33%;
  overflow: auto;

  @include flexbox {
    flex: 0 1 auto;
  }
}


/* Alternate styles */

.k-alert--success {
  @include alert-variant($alert-success-background, $alert-success-color);
}

.k-alert--info {
  @include alert-variant($alert-info-background, $alert-info-color);
}

.k-alert--warning {
  @include alert-variant($alert-warning-background, $alert-warning-color);
}

.k-alert--danger {
  @include alert-variant($alert-danger-background, $alert-danger-color);
}

/**
 * 1. Scroll when items don't fit parent anymore
 * 2. Hide scroll when resizing to avoid weird jumps on OSX
 * 3. Don't flex in the layout structure
 */

.k-breadcrumb {
  @include border(bottom, $breadcrumb-border);
  overflow: auto; /* 1 */
  background: $breadcrumb-background;
  color: $breadcrumb-color;

  @include resizing {
    overflow: hidden; /* 2 */
  }

  @include flexbox(0, 0) {
    flex: 0 0 auto; /* 3 */
  }


  /**
   * List
   *
   * 1. Reset unordered list styling
   * 2. Flexing items to get overflow ellipsis
   */

  ul {
    @include reset; /* 1 */

    @include flexbox(0, 0) {
      display: flex; /* 2 */
    }
  }


  /**
   * Styling list items
   *
   * 1. Set z-index to 0 so the :after psuedo element will be on top
   * 2. Make room for the psuedo element separator
   * 3. Make each item at least one breadcrumb size wide
   * 4. Hide by default so we can show only a few on mobile / flexbox
   * 5. Hide overflowing content
   * 6. Proper spacing for non-flexbox use
   */

  li {
    position: relative; /* 1 */
    z-index: 0; /* 1 */
    padding-left: $breadcrumb-divider-size; /* 2 */
    min-width: $breadcrumb-size; /* 3 */
    display: none; /* 4 */
    overflow: hidden; /* 5 */
    vertical-align: middle; /* 6 */

    @include rtl {
      padding-left: 0;
      padding-right: $breadcrumb-divider-size;
    }


    /* Flexing items to get overflow ellipsis */

    @include flexbox(0, 0) {
      flex: 0 1 auto;
    }


    /* Display on larger screens but only when flexbox is available */

    @include flexbox($breadcrumb-query, 0) {
      display: inline-block;
    }


    /* List item separator */

    @include psuedo(before, $content: false) {
      top: 0;
      bottom: 0;
      left: 0;
      width: $breadcrumb-divider-size;
      padding-top: $breadcrumb-size / 4;
      padding-bottom: $breadcrumb-size / 4;
      text-align: center;
      line-height: $breadcrumb-size / 2;
      content: $breadcrumb-divider-content;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* No separator on first item */

    &:first-child {
      padding-left: 0;

      @include rtl {
        padding-right: 0;
      }

      &:before {
        display: none;
      }
    }


    /**
     * Hover
     *
     * 1. Reset flexbox so the whole item is visible
          No :focus since <li> can't be focused
     * 2. Wrap the active item when another item is being hovered
     */

    &:hover {
      overflow: visible;

      @include flexbox(0, 0) {
        flex: 0 0 auto; /* 1 */
      }

      &:not(.k-breadcrumb__home) ~ li {
        @include flexbox(0, 0) {
          flex: 0 1 auto; /* 2 */
        }
      }
    }
  }


  /**
   * Links
   *
   * 1. Full background hover
   */

  a {
    color: $breadcrumb-color;
    cursor: pointer;

    &:focus,
    &:hover {
      background: $breadcrumb-hover-background; /* 1 */
    }
  }


  /**
   * Home and active elements
   *
   * 1. Always display these items
   * 2. No flexing for these items so they are fully readable
   */

  .k-breadcrumb__active,
  .k-breadcrumb__home {
    display: inline-block; /* 1 */
    overflow: visible; /* 1 */

    @include flexbox(0, 0) {
      flex: 0 0 auto; /* 2 */
    }
  }


  /**
   * One but last item
   *
   * Show this item on mobile to get a sense of where you are
   * Only do this when flexbox is available
   *
   * 1. Set 'missing' content
   * 2. Reset to default content on wider screens
   */

  li:nth-last-child(2) {
    @include flexbox(0, 0) {
      display: inline-block;

      &:before {
        content: $breadcrumb-missing-content; /* 1 */

        @include breakpoint($breadcrumb-query) {
          content: $breadcrumb-divider-content; /* 2 */
        }
      }
    }
  }


  /* Active item */

  .k-breadcrumb__active {
    .k-breadcrumb__content {
      font-weight: $bold;
    }
  }

}


/* Single breadcrumb items */

.k-breadcrumb__content {
  position: relative;
  z-index: 2;
  display: block;
  padding: round($breadcrumb-size / 4);
  min-width: $breadcrumb-size;
  min-height: $breadcrumb-size;
  overflow: hidden;
  text-align: center;
  line-height: floor($breadcrumb-size / 2);
  text-decoration: none;
  white-space: nowrap;
  text-overflow: ellipsis;
}

/**
 * 1. For input.k-button
 * 2. Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
 */

.k-button {
  display: inline-block;
  margin-bottom: 0; /* 1 */
  font-weight: $regular;
  text-align: center;
  vertical-align: middle;
  touch-action: manipulation;
  cursor: pointer;
  background-image: none; /* 2 */
  border: 1px solid transparent;
  white-space: nowrap;
  box-shadow: none;
  text-shadow: none;
  user-select: none;
  border-radius: $border-radius;
  margin-right: $spacing-tiny;
  line-height: $line-height;
  padding: $spacing-base-vertical $spacing-base-horizontal;

  &:last-child {
    margin-right: 0;
  }

  @include rtl {
    margin-right: 0;
    margin-left: $spacing-tiny;

    &:last-child {
      margin-left: 0;
    }
  }

  &,
  &:active,
  &.k-is-active {
    &:focus,
    &.focus {
      @include tab-focus;
    }
  }

  &:hover,
  &:focus {
    color: $btn-default-color;
    text-decoration: none;
  }

  &:active,
  &.k-is-active {
    outline: 0;
    background-image: none;
  }

  &[disabled],
  &.k-is-disabled {
    cursor: $cursor-disabled;
    opacity: 0.65;
  }
}


/**
 * 1. Remove underline when button is an `<a>` element
 * 2. Future-proof disabling of clicks on elements
 */

.k-button {
  &, &:hover, &:focus, &:active {
    text-decoration: none; /* 1 */
  }
}

button.k-button.k-is-disabled {
  pointer-events: none; /* 2 */
}


/* Alternate buttons */

.k-button--default {
  @include button-variant($btn-default-color, $btn-default-background, $btn-default-border);
}

.k-button--primary {
  @include button-variant($btn-primary-color, $btn-primary-background, $btn-primary-border);
}

.k-button--success {
  @include button-variant($btn-success-color, $btn-success-background, $btn-success-border);
}

.k-button--info {
  @include button-variant($btn-info-color, $btn-info-background, $btn-info-border);
}

.k-button--warning {
  @include button-variant($btn-warning-color, $btn-warning-background, $btn-warning-border);
}

.k-button--danger {
  @include button-variant($btn-danger-color, $btn-danger-background, $btn-danger-border);
}


/* Link buttons */

.k-button--link {
  color: $color-type;
  font-weight: normal;
  border-radius: 0;
  padding-left: 0;
  padding-right: 0;

  &,
  &:active,
  &.k-is-active,
  &[disabled],
  &.k-is-disabled {
    background-color: transparent;
    box-shadow: none;
  }

  &,
  &:hover,
  &:focus,
  &:active {
    border-color: transparent;
  }

  &:hover,
  &:focus {
    color: $color-type;
    text-decoration: underline;
    background-color: transparent;
  }

  &[disabled],
  &.k-is-disabled {
    &:hover,
    &:focus {
      color: $btn-link-disabled-color;
      text-decoration: none;
    }
  }
}


/* Block button */

.k-button--block {
  display: block;
  width: 100%;
}

.k-button--tiny {
  border-radius: $border-radius-small;
  font-size: $font-size-tiny;
  line-height: $line-height-tiny;
  padding: $spacing-tiny-vertical $spacing-tiny-horizontal;
}

.k-button--small {
  border-radius: $border-radius-small;
  font-size: $font-size-small;
  line-height: $line-height-small;
  padding: $spacing-small-vertical $spacing-small-horizontal;
}

.k-button--large {
  border-radius: $border-radius-large;
  font-size: $font-size-large;
  line-height: $line-height-large;
  padding: $spacing-large-vertical $spacing-large-horizontal;
}


/* Vertically space out multiple block buttons */

.k-button--block + .k-button--block {
  margin-top: 5px;
}


/* Specificity overrides */

input[type="submit"],
input[type="reset"],
input[type="button"] {
  &.k-button--block {
    width: 100%;
  }
}

.k-button-group {
  position: relative;
  display: inline-block;
  vertical-align: middle;

  & > .k-button {
    position: relative;
    float: left;
    margin: 0;

    @include rtl {
      float: right;
    }


    /* Bring the "active" button to the front */

    &:hover,
    &:focus,
    &:active,
    &.k-is-active {
      z-index: 2;
    }
  }


  /* Prevent double borders when buttons are next to each other */

  .k-button + .k-button,
  .k-button + .k-button-group,
  .k-button-group + .k-button,
  .k-button-group + .k-button-group {
    margin-left: -1px;

    &:first-child {
      margin-left: 0;
    }
  }

  @include rtl {
    .k-button + .k-button,
    .k-button + .k-button-group,
    .k-button-group + .k-button,
    .k-button-group + .k-button-group {
      margin-left: 0;
      margin-right: -1px;

      &:first-child {
        margin-right: 0;
      }
    }
  }

  & > .k-button:not(:first-child):not(:last-child) {
    border-radius: 0;
  }

  & > .k-button:first-child:not(:last-child) {
    @include border-right-radius(0);

    @include rtl {
      @include border-right-radius($border-radius);
      @include border-left-radius(0);

      &.k-button--tiny {
        @include border-right-radius($border-radius-small);
      }

      &.k-button--small {
        @include border-right-radius($border-radius-small);
      }

      &.k-button--large {
        @include border-right-radius($border-radius-large);
      }
    }
  }

  & > .k-button:last-child:not(:first-child) {
    @include border-left-radius(0);

    @include rtl {
      @include border-left-radius($border-radius);
      @include border-right-radius(0);

      &.k-button--tiny {
        @include border-left-radius($border-radius-small);
      }

      &.k-button--small {
        @include border-left-radius($border-radius-small);
      }

      &.k-button--large {
        @include border-left-radius($border-radius-large);
      }
    }
  }
}

.k-card {
  position: relative;
  display: block;
  width: 100%;
  @include content-block;
}

.k-card--center {
  .k-card__body {
    text-align: center;
  }
}

.k-card--rounded {
  .k-card__body {
    border-radius: $card-radius;
  }

  .k-card__caption {
    border-bottom-left-radius: $card-radius - 1px;
    border-bottom-right-radius: $card-radius - 1px;
  }

  .k-card__image img,
  .k-card__image--background,
  .k-card__image--spaced-background,
  .k-card__header,
  .k-card__section,
  .k-card__footer {
    overflow: hidden;

    &:only-child {
      border-radius: $card-radius - 1px;
    }
    &:first-child {
      border-top-left-radius: $card-radius - 1px;
      border-top-right-radius: $card-radius - 1px;
    }
    &:last-child {
      border-bottom-left-radius: $card-radius - 1px;
      border-bottom-right-radius: $card-radius - 1px;
    }
  }

  .k-card__image--background {
    border-radius: $card-radius - 1px;
  }

  .k-card__image--spaced-background {
    border-radius: $card-radius - 2px;
  }
}


/**
 * Card body
 */

.k-card__body {
  position: relative;
  display: block;
  backface-visibility: hidden;
  background: $card-background;
  border: 1px solid $card-border;
  color: $card-color;
  text-decoration: none;
  @include transition;

  &:focus,
  &:hover {
    border-color: $card-hover-border;

    .k-card__header {
      border-color: $card-header-hover-border;
    }

    .k-card__footer {
      border-color: $card-footer-hover-border;
    }
  }
}


/**
 * Card image
 *
 * 1. For when background image is used
 * 2. Cover complete card minus border
 * 3. Cover complete card minus border and 1pixel spacing
 */

.k-card__image {
  z-index: 0;
  display: block;
  overflow: hidden;
  @include transition;
}

.k-card__image--background {
  background-position: center; /* 1 */
  background-repeat: no-repeat; /* 1 */
  background-size: cover; /* 1 */
  @include coverall(0,1px,1px,1px,1px); /* 2 */
}

.k-card__image--spaced-background {
  @include coverall(0,2px,2px,2px,2px); /* 3 */
}


/**
 * Card title
 */

.k-card__header {
  position: relative;
  z-index: 1;
  display: block;
  padding: 10px;
  font-weight: $bold;
  background: $card-header-background;
  border-bottom: 1px solid $card-header-border;
  color: $card-header-color;
  text-decoration: none;
  @include transition;

  &:last-child {
    border-bottom: none;
  }

  &:not(:first-child) {
    border-top: 1px solid $card-header-border;
  }

  &:focus,
  &:hover {
    border-color: $card-header-hover-border;
  }
}


/**
 * Card section
 *
 * 1. Use margin instead of padding to keep border-radius on rounded cards
 */

.k-card__section {
  position: relative;
  z-index: 1;
  display: block;
  padding: 10px;
  text-decoration: none;
  @include transition;
}

.k-card__section--small-spacing {
  padding: 0;
  margin: 1px; /* 1 */
}


/**
 * Card footer
 */

.k-card__footer {
  position: relative;
  z-index: 1;
  display: block;
  padding: 10px;
  background: $card-footer-background;
  border-top: 1px solid $card-footer-border;
  color: $card-footer-color;
  text-decoration: none;
  @include transition;

  &:first-child {
    border-top: none;
  }

  &:not(:last-child) {
    border-bottom: 1px solid $card-footer-border;
  }

  &:focus,
  &:hover {
    border-color: $card-footer-hover-border;
  }
}


/**
 * Card caption
 */

.k-card__caption {
  display: block;
  width: 100%;
  padding: 1em 10px;
  font-weight: $regular;
  text-decoration: none;
  @include transition;
}

.k-card__caption--overflow {
  height: 3em;
  line-height: 1em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}


/**
 * Selected card
 */

.k-card.k-is-selected {
  .k-card__body {
    border-color: $card-selected-border;

    &:not(:last-child) {
      border-bottom-left-radius: 0;
      border-bottom-right-radius: 0;
    }

    &:only-child {
      .k-card__header {
        background: $card-selected-background;
        color: $card-selected-color;
      }
    }
  }

  .k-card__header,
  .k-card__footer,
  .k-card__caption {
    border-color: $card-selected-border;
    background: $card-selected-background;
    color: $card-selected-color;
  }
}

.datepicker {
  direction: ltr;

  &.datepicker-inline {
    width: 220px;
  }

  &.datepicker-rtl {
    direction: rtl;

    table tr td span {
      float: right;
    }
  }

  &.datepicker-dropdown {
    position: absolute;
    top: 0;
    left: 0;
    z-index: $zindex-dropdown;
    padding: 5px 0;
    margin: 2px 0 0;
    list-style: none;
    background-color: $dropdown-background;
    border: 1px solid $dropdown-border;
    border-radius: $border-radius;
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.175);
    background-clip: padding-box;

    &:before {
      content: '';
      display: inline-block;
      border-left: 7px solid transparent;
      border-right: 7px solid transparent;
      border-bottom: 7px solid #ccc;
      border-top: 0;
      border-bottom-color: rgba(0, 0, 0, 0.2);
      position: absolute;
    }
    &:after {
      content: '';
      display: inline-block;
      border-left: 6px solid transparent;
      border-right: 6px solid transparent;
      border-bottom: 6px solid white;
      border-top: 0;
      position: absolute;
    }

    &.datepicker-orient-left:before {
      left: 6px;
    }

    &.datepicker-orient-left:after {
      left: 7px;
    }

    &.datepicker-orient-right:before {
      right: 6px;
    }

    &.datepicker-orient-right:after {
      right: 7px;
    }

    &.datepicker-orient-top:before {
      top: -7px;
    }

    &.datepicker-orient-top:after {
      top: -6px;
    }

    &.datepicker-orient-bottom:before {
      bottom: -7px;
      border-bottom: 0;
      border-top: 7px solid #999;
    }

    &.datepicker-orient-bottom:after {
      bottom: -6px;
      border-bottom: 0;
      border-top: 6px solid white;
    }
  }

  & > div {
    display: none;
  }

  &.days div.datepicker-days {
    display: block;
  }

  &.months div.datepicker-months {
    display: block;
  }

  &.years div.datepicker-years {
    display: block;
  }

  table {
    margin: 0;
    user-select: none;
  }

  td,
  th {
    text-align: center;
    width: 20px;
    height: 20px;
    border: none;
    border-radius: 4px;
  }


  /**
   * Inline display inside a table presents some problems with
   * border and background colors.
   */

  .table-striped & table tr {
    td, th {
      background-color: transparent;
    }
  }

  table tr td {
    &.day:hover {
      background: $color-primary-plus-2;
      cursor: pointer;
    }

    &.old,
    &.new {
      color: $color-primary-min-6;
    }

    &.disabled,
    &.disabled:hover {
      background: none;
      color: $color-primary-min-6;
      cursor: default;
    }


    /**
     * 1. Add last
     */

    &.today,
    &.today:hover,
    &.today.disabled,
    &.today.disabled:hover {
      $todayBackground: lighten(orange, 30%);
      background: $todayBackground;
      color: #000; /* 1 */
    }


    /* Thank bootstrap 2.0 for this selector... */

    &.today:hover:hover {
      color: #000;
    }

    &.today.active:hover {
      color: #fff;
    }

    &.range,
    &.range:hover,
    &.range.disabled,
    &.range.disabled:hover {
      background: $color-primary-plus-2;
      border-radius: 0;
    }

    &.range.today,
    &.range.today:hover,
    &.range.today.disabled,
    &.range.today.disabled:hover {
      $todayBackground: mix(orange, $color-primary-plus-2, 50%);
      background: $todayBackground;
      border-radius: 0;
    }

    &.selected,
    &.selected:hover,
    &.selected.disabled,
    &.selected.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
      background: lighten($color-primary-min-6, 10);
    }

    &.active,
    &.active:hover,
    &.active.disabled,
    &.active.disabled:hover {
      color: #fff;
      text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
      background: $btn-primary-background;
    }

    span {
      display: block;
      width: 23%;
      height: 54px;
      line-height: 54px;
      float: left;
      margin: 1%;
      cursor: pointer;
      border-radius: 4px;

      &:hover {
        background: $color-primary-plus-2;
      }

      &.disabled,
      &.disabled:hover {
        background: none;
        color: $color-primary-min-6;
        cursor: default;
      }

      &.active,
      &.active:hover,
      &.active.disabled,
      &.active.disabled:hover {
        color: #fff;
        text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
        background: $btn-primary-background;
      }

      &.old,
      &.new {
        color: $color-primary-min-6;
      }
    }
  }

  th.datepicker-switch {
    width: 145px;
  }

  thead tr:first-child th,
  tfoot tr th {
    cursor: pointer;
    &:hover {
      background: $color-primary-plus-2;
    }
  }


  /* Basic styling for calendar-week cells */

  .cw {
    font-size: $font-size-small;
    width: 12px;
    padding: 0 2px 0 5px;
    vertical-align: middle;
  }

  thead tr:first-child th.cw {
    cursor: default;
    background-color: transparent;
  }
}

.input-append,
.input-prepend {
  &.date {
    .add-on i {
      display: block;
      cursor: pointer;
      width: 16px;
      height: 16px;
    }
  }
}

.input-daterange {
  input {
    text-align: center;
  }

  input:first-child {
    border-radius: 3px 0 0 3px;
  }

  input:last-child {
    border-radius: 0 3px 3px 0;
  }

  .add-on {
    display: inline-block;
    width: auto;
    min-width: 16px;
    height: $line-height;
    padding: 4px 5px;
    font-weight: normal;
    line-height: $line-height;
    text-align: center;
    text-shadow: 0 1px 0 white;
    vertical-align: middle;
    background-color: $color-primary-plus-2;
    border: 1px solid #ccc;
    margin-left: -5px;
    margin-right: -5px;
  }
}

/* Dropdown arrow/caret */

.k-caret {
  display: inline-block;
  width: 0;
  height: 0;
  margin-left: 2px;
  vertical-align: middle;
  border-top:   $caret-width-base dashed;
  border-right: $caret-width-base solid transparent;
  border-left:  $caret-width-base solid transparent;

  @include rtl {
    margin-left: 0;
    margin-right: 2px;
  }
}


/* The dropdown wrapper (div) */

.k-dropdown {
  position: relative;
}


/* The dropdown toggle */

.k-dropdown__toggle {
  img {
    max-width: 16px;
  }
}


/**
 * The dropdown menu (ul)
 *
 * 1. Hide by default, but block on "open" of the menu
 * 2. Ensures proper alignment if parent has it changed (e.g., modal footer)
 */

.k-dropdown__menu {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: $zindex-dropdown;
  display: none; /* 1 */
  float: left;
  min-width: 160px;
  text-align: left; /* 2 */
  background-color: $dropdown-background;
  border: 1px solid $dropdown-border;
  border-radius: $border-radius;
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.175);
  background-clip: padding-box;


  /* Winning over .koowa ul */

  &.k-dropdown__menu {
    padding: 5px 0;
    margin: 2px 0 0;
    list-style: none;
  }


  /**
    * Links within the dropdown menu
    *
    * 1. prevent links from randomly breaking onto new lines
    */

  a {
    display: block;
    padding: 5px 15px;
    clear: both;
    font-weight: normal;
    line-height: $line-height;
    color: $dropdown-link-color;
    white-space: nowrap; /* 1 */
  }


  /* RTL */

  @include rtl {
    left: auto;
    right: 0;
  }
}


/**
 * Dividers (basically an hr) within the dropdown
 */

.k-dropdown__divider {
  height: 1px;
  margin: 5px 0;
  overflow: hidden;
  background-color: #e5e5e5;
}


/* Hover / Focus state */

.k-dropdown__menu a {
  &:hover,
  &:focus {
    text-decoration: none;
    color: $dropdown-link-hover-color;
    background-color: $dropdown-link-hover-background;
  }
}


/* Active state */

.k-dropdown__menu .k-is-active a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-active-color;
    text-decoration: none;
    outline: 0;
    background-color: $dropdown-link-active-background;
  }
}


/**
 * Disabled state
 *
 * Gray out text and ensure the hover/focus state remains gray
 */

.k-dropdown__menu .k-is-disabled a {
  &,
  &:hover,
  &:focus {
    color: $dropdown-link-disabled-color;
  }


  /**
   * Nuke hover/focus effects
   *
   * 1. Remove CSS gradient
   */

  &:hover,
  &:focus {
    text-decoration: none;
    background-color: transparent;
    background-image: none; /* 1 */
    cursor: $cursor-disabled;
  }
}


/* Open state for the dropdown */

.k-dropdown.k-is-open {


  /* Show the menu */

  & > .k-dropdown__menu {
    display: block;
  }


  /* Remove the outline when :focus is triggered */

  & > a {
    outline: 0;
  }
}


/* Backdrop to catch body clicks on mobile, etc. */

.k-dropdown-backdrop {
  position: fixed;
  left: 0;
  right: 0;
  bottom: 0;
  top: 0;
  z-index: ($zindex-dropdown - 10);
}


/**
 * Dropup
 *
 * Allow for dropdowns to go bottom up (aka, dropup-menu)
 */

.k-dropdown--dropup {


  /* Reverse the caret */

  .k-caret {
    border-top: 0;
    border-bottom: $caret-width-base dashed;
    content: "";
  }


  /* Different positioning for bottom up menu */

  .k-dropdown__menu {
    top: auto;
    bottom: 100%;
    margin-bottom: 2px;
  }
}

.k-dropdown__menu--grid {
  width: 212px;


  /* override above default settings */

  &.k-dropdown__menu--grid {
    padding: 5px;

    @include rtl {
      padding: 5px;
    }
  }

  img {
    max-width: 16px;
    max-height: 16px;
  }

  li {
    float: left;
    width: 40px;
    height: 40px;
    margin: 0;
    padding: 0;
    display: block;

    @include rtl {
      float: right;
    }

    a {
      display: block;
      padding: 0;
      margin: 0;
      width: 40px;
      height: 40px;
      text-align: center;
      border-radius: 3px;
      float: left;

      [class^="k-icon-document-"],
      [class*=" k-icon-document-"] {
        display: block;
        vertical-align: middle;
        margin: ((40px - 16px)/2) auto;
      }
    }
  }

  .k-dropdown__block-item {
    min-width: 100%;
    clear: both;
    height: auto;
    margin: 5px 0 -5px;

    a {
      position: relative;
      height: auto;
      width: auto;
      float: none;
      border-top: 1px solid #e5e5e5;
      text-align: left;
      margin: 0 -5px;
      padding: 5px 15px;
      border-radius: 0 0 $border-radius $border-radius;

      @include rtl {
        text-align: right;
      }
    }
  }
}

.k-empty-state {
  margin: 0;
  font-size: $font-size-large;
  line-height: $line-height-large;
  text-align: center;
  background: $empty-state-background;
  color: $empty-state-color;
  padding: $empty-state-spacing;

  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    justify-content: center;
  }

  p {
    @include content-block(1em);
  }
}

.k-form-block {
  @include border(all, $form-block-border);
  background: $form-block-background;
}


/* The header */

.k-form-block__header {
  @include border(bottom, $form-block-header-border);
  padding: $form-block-header-spacing;
  background: $form-block-header-background;
  font-weight: $bold;
}


/* The content */

.k-form-block__content {
  padding: $form-block-content-spacing;
}

/**
 * 1. Set width and max-width to 100% to override custom template styling
 * 1. Make inputs at least the height of their button counterpart (base line-height + padding + border)
 * 2. Reset unusual Firefox-on-Android default style; see https://github.com/necolas/normalize.css/issues/214
 * 3. Note: This has no effect on <select>s in some browsers, due to the limited stylability of <select>s in CSS.
 * 4. Customize the `:focus` state to imitate native WebKit styles.
 * 5. Unstyle the caret on `<select>`s in IE10+.
 * 6. iOS fix for unreadable disabled content; see https://github.com/twbs/bootstrap/issues/11655
 */

.k-form-control {
  display: block;
  width: 100%; /* 1 */
  max-width: 100%; /* 1 */
  height: $input-height-base; /* 2 */
  padding: $spacing-base-vertical $spacing-base-horizontal;
  font-size: $font-size;
  line-height: $line-height;
  color: $input-color;
  background-color: $input-background;
  background-image: none; /* 3 */
  border: 1px solid $input-border;
  border-radius: $input-border-radius; /* 4 */
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
  transition: border-color ease-in-out 0.15s, box-shadow ease-in-out 0.15s;


  /* 5 */

  @include form-control-focus;


  /* Placeholders */

  &::-moz-placeholder {
    color: $input-color-placeholder;
    opacity: 1;
  }

  &:-ms-input-placeholder {
    color: $input-color-placeholder;
  }

  &::-webkit-input-placeholder  {
    color: $input-color-placeholder;
  }


  /* 6 */

  &::-ms-expand {
    border: 0;
    background-color: transparent;
  }


  /* Disabled and read-only inputs */

  &[disabled],
  &[readonly],
  fieldset[disabled] & {
    background-color: $input-background-disabled;
    opacity: 1; /* 7 */
  }

  &[disabled],
  fieldset[disabled] & {
    cursor: $cursor-disabled;
  }
}


/* Reset height for `textarea`s */

textarea.k-form-control {
  height: auto;
}


/**
 * Search inputs in iOS
 *
 * This overrides the extra rounded corners on search inputs in iOS so that our
 * `.k-form-control` class can properly style them. Note that this cannot simply
 * be added to `.k-form-control` as it's not specific enough. For details, see
 * https://github.com/twbs/bootstrap/issues/11586.
 */

input[type="search"] {
  -webkit-appearance: none;
}


/**
 * Special styles for iOS temporal inputs
 *
 * In Mobile Safari, setting `display: block` on temporal inputs causes the
 * text within the input to become vertically misaligned. As a workaround, we
 * set a pixel line-height that matches the given height of the input, but only
 * for Safari. See https://bugs.webkit.org/show_bug.cgi?id=139848
 *
 * Note that as of 8.3, iOS doesn't support `datetime` or `week`.
 */

@media screen and (-webkit-min-device-pixel-ratio: 0) {
  input[type="date"],
  input[type="time"],
  input[type="datetime-local"],
  input[type="month"] {
    &.k-form-control {
      line-height: $input-height-base;
    }

    .k-input-group--small & {
      line-height: $input-height-small;
    }

    .k-input-group--large & {
      line-height: $input-height-large;
    }
  }
}

/* Shared styling between radio boxes and checkboxes */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {

    /**
     * 1. Resetting the default styling used by browsers. Don't do this for -moz-
     *    because the checkbox and radio box will look very ugly
     * 2. Positioning relative to position psuedo before element nicely
     * 5. Indicate that element is clickable
     */

    -webkit-appearance: none; /* 1 */
    /* -moz-appearance: don't set this; /* 1 */
    position: relative; /* 2 */
    height: $form-option-size;
    width: $form-option-size;
    margin: 0 .33em 0 0;
    border: $form-option-border-size solid $form-option-border;
    padding: 0;
    background: $form-option-background;
    display: inline-block;
    vertical-align: middle;
    cursor: pointer; /* 5 */
    box-shadow: $form-option-shadow;
    @include transition(background);


    /* Hover to indicate element is clickable */

    &:hover {
      background: #f1f1f1;
    }


    /* Set disabled border color a bit lighter so it won't appear to be larger */

    &:disabled {
      border-color: #bbb;
    }


    /**
     * 1. Position psuedo element absolute to have full control
     * 2. Position top-left, margin, width and height will take care of centering
     * 3. Initially hide the psuedo element
     * 4. Show the psuedo element when item is checked
     */

    &:before {
      position: absolute; /* 1 */
      top: 0; /* 2 */
      left: 0; /* 2 */
      visibility: hidden; /* 3 */
      transform: scale(0,0); /* 3 */
      text-align: center;
      vertical-align: middle;
      background: transparent;
      @include transition;
    }

    &:checked:before {
      visibility: visible; /* 4 */
      transform: scale(1,1); /* 4 */
    }
  }


  /* Put some space between the second box and the first label */

  label {

    /**
     * <input id="example1" />
     * <label for="example1">Label 1</label>
     * <input id="example2" />
     * <label for="example2">Label 2</label>
     */

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: 1em;
    }


    /**
     * <label><input id="example3" /> Label 1</label>
     * <label><input id="example4" /> Label 2</label>
     */

    & + label {
      margin-left: 1em;
    }
  }
}


/* RTL */

@include modernizr-rtl('.k-appearance.k-checked') {
  input[type="checkbox"],
  input[type="radio"] {
    margin: 0 0 0 .33em;

    &:before {
      left: auto;
      right: 0;
    }
  }

  label {

    & + input[type="checkbox"],
    & + input[type="radio"] {
      margin-left: auto;
      margin-right: 1em;
    }

    & + label {
      margin-left: auto;
      margin-right: 1em;
    }
  }
}


/**
 * Checkbox
 *
 * 1. Remove rounded corners by default
 * 2. Display a check icon in the psuedo before element
 * 3. Height of the box minus the border size
 * 4. Height of the psuedo element minus a 1px padding/margin to move icon
      away from the side of the box
 * 5. Set the color of the check icon
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="checkbox"] {
    border-radius: 0; /* 1 */

    @include icon-before(check, true) {
      /* 2 */
      width: $form-option-size - ($form-option-border-size * 2); /* 3 */
      height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      line-height: $form-option-size - ($form-option-border-size * 2); /* 3 */
      font-size: $form-option-size - ($form-option-border-size * 4); /* 4 */
      color: $color-accent; /* 5 */
    }
  }
}


/**
 * Radio
 *
 * 1. Making the radio box round
 * 2. Adding content to display the psuedo element
 * 3. Small inner circle, scaling with the box itself
 * 4. positioning the inner circle
 * 5. Making inner circle actually round
 */

@include modernizr('.k-appearance.k-checked') {
  input[type="radio"] {
    border-radius: 50%; /* 1 */

    &:before {
      content: " "; /* 2 */
      width: $form-option-size - ($form-option-border-size * 8); /* 3 */
      height: $form-option-size - ($form-option-border-size * 8); /* 3 */
      margin: 3px; /* 4 */
      background: $form-option-color;
      border-radius: 50%; /* 5 */
    }
  }
}

.k-has-success {
  @include form-control-validation($state-success-color, $state-success-color, $state-success-background);
}
.k-has-warning {
  @include form-control-validation($state-warning-color, $state-warning-color, $state-warning-background);
}
.k-has-error {
  @include form-control-validation($state-danger-color, $state-danger-color, $state-danger-background);
}

/**
 * Static form control text
 *
 * Apply class to a `p` element to make any string of text align with labels in
 * a horizontal form layout.
 *
 * 1. Size it appropriately next to real form controls
 * 2. Remove default margin from `p`
 */

.k-static-form-label {
  display: inline-block;
  vertical-align: middle;
  max-width: 100%; /* 1 */
  font-weight: bold;
  @include content-block(5px);
}

/**
 * 1. For dropdowns
 * 2. Prevent input groups from inheriting border styles from table cells when placed within a table
 * 3. Ensure that the input is always above the *appended* addon button for proper border colors.
 * 4. IE9 fubars the placeholder attribute in text inputs and the arrows on
      select elements in input groups. To fix it, we float the input.
 * 5. Put focused element on top of the rest
 */

.k-input-group {
  position: relative; /* 1 */
  display: table;
  width: 100%;
  border-collapse: separate; /* 2 */


  /* Flexbox input groups for better overflow styling */

  @include flexbox(0, 0) {
    display: flex;
  }


  /* The form control */

  .k-form-control {

    /* 3 */
    position: relative;
    z-index: 2;

    /* 4 */
    float: left;

    @include rtl {
      float: right;
    }

    width: 100%;
    margin-bottom: 0;

    &:focus {
      z-index: 3; /* 5 */
    }


    /* Flexbox input groups for better overflow styling */

    @include flexbox(0, 0) {
      width: auto;
      min-width: 15%;
    }
  }


  /* The button */

  .k-input-group__button {
    .k-button {
      height: $input-height-base;
    }
  }
}


/* Sizes */

.k-input-group--large {
  & > .k-form-control,
  & > .select2-container--bootstrap .select2-selection--single,
  & > .k-input-group__addon,
  & > .k-input-group__button > .k-button {
    height: $input-height-large;
    padding: $spacing-large-vertical $spacing-large-horizontal;
    font-size: $font-size-large;
    line-height: $line-height-large;
    border-radius: $border-radius-large;

    @include rtl {
      border-radius: $border-radius-large;
    }
  }

  & > .select2-container--bootstrap .select2-selection--single .select2-selection--rendered {
    line-height: $line-height-large;
  }

  select.k-form-control {
    height: $input-height-large;
    line-height: $input-height-large;

    @include rtl {
      direction: rtl;
    }
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

.k-input-group--small {
  & > .k-form-control,
  & > .select2-container--bootstrap .select2-selection--single,
  & > .k-input-group__addon,
  & > .k-input-group__button > .k-button {
    height: $input-height-small;
    padding: $spacing-small-vertical $spacing-small-horizontal;
    font-size: $font-size-small;
    line-height: $line-height-small;
    border-radius: $border-radius-small;

    @include rtl {
      border-radius: $border-radius-small;
    }
  }

  & > .select2-container--bootstrap .select2-selection--single .select2-selection--rendered {
    line-height: $line-height-small;
  }

  select.k-form-control {
    height: $input-height-small;
    line-height: $input-height-small;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}


/* Input group parts */

.k-input-group__addon,
.k-input-group__button,
.k-input-group .k-form-control {
  display: table-cell;

  &:not(:first-child):not(:last-child) {
    border-radius: 0;
  }


  /* Flexbox input groups for better overflow styling */

  @include flexbox(0, 0) {
    display: block;
    flex: 1 1 auto;
  }
}

.k-input-group .select2-container {
  &:not(:first-child):not(:last-child) {
    .select2-selection--single {
      border-radius: 0;
    }
  }

  &.select2-container--open,
  &.select2-container--focus {
    z-index: 3;
  }
}


/**
 * Addon and addon wrapper for buttons
 *
 * 1. Match the inputs
 */

.k-input-group__addon,
.k-input-group__button {
  width: 1%;
  white-space: nowrap;
  vertical-align: middle; /* 1 */


  /* Flexbox input groups for better overflow styling */

  @include flexbox(0, 0) {
    width: auto;
    display: block;
    flex: 0 0 auto;
  }
}


/* Text addon */

.k-input-group__addon {
  font-size: $font-size;
  line-height: $line-height;
  height: $input-height-base;
  padding: $spacing-base-vertical $spacing-base-horizontal;
  font-weight: normal;
  color: $input-color;
  text-align: center;
  background-color: $input-group-addon-bg;
  border: 1px solid $input-group-addon-border-color;
  border-radius: $input-border-radius;
  overflow: hidden;
  text-overflow: ellipsis;;


  /* Nuke default margins from checkboxes and radios to vertically center within. */

  input[type="radio"],
  input[type="checkbox"] {
    margin-top: 0;
  }
}


/* Reset rounded corners */

.k-input-group .k-form-control:first-child,
.k-input-group__addon:first-child,
.k-input-group__button:first-child > .k-button,
.k-input-group__button:first-child > .k-button-group > .k-button,
.k-input-group__button:first-child > .k-dropdown__toggle,
.k-input-group__button:last-child > .k-button:not(:last-child):not(.k-dropdown__toggle),
.k-input-group__button:last-child > .k-button-group:not(:last-child) > .k-button {
  @include border-right-radius(0);
}

@include rtl {
  .k-input-group .k-form-control:first-child,
  .k-input-group__addon:first-child,
  .k-input-group__button:first-child > .k-button,
  .k-input-group__button:first-child > .k-button-group > .k-button,
  .k-input-group__button:first-child > .k-dropdown__toggle,
  .k-input-group__button:last-child > .k-button:not(:last-child):not(.k-dropdown__toggle),
  .k-input-group__button:last-child > .k-button-group:not(:last-child) > .k-button {
    @include border-right-radius($input-border-radius);
    @include border-left-radius(0);
  }
}

.k-input-group__addon:first-child {
  border-right: none;

  @include rtl {
    border-right: 1px solid $input-border;
    border-left: none;
  }
}

.k-input-group .k-form-control:last-child,
.k-input-group__addon:last-child,
.k-input-group__button:last-child > .k-button,
.k-input-group__button:last-child > .k-button-group > .k-button,
.k-input-group__button:last-child > .k-dropdown__toggle,
.k-input-group__button:first-child > .k-button:not(:first-child),
.k-input-group__button:first-child > .k-button-group:not(:first-child) > .k-button {
  @include border-left-radius(0);
}

@include rtl {
  .k-input-group .k-form-control:last-child,
  .k-input-group__addon:last-child,
  .k-input-group__button:last-child > .k-button,
  .k-input-group__button:last-child > .k-button-group > .k-button,
  .k-input-group__button:last-child > .k-dropdown__toggle,
  .k-input-group__button:first-child > .k-button:not(:first-child),
  .k-input-group__button:first-child > .k-button-group:not(:first-child) > .k-button {
    @include border-left-radius($input-border-radius);
    @include border-right-radius(0);
  }
}

.k-input-group__addon:last-child {
  border-left: 1px solid $input-border;

  @include rtl {
    border-left-width: 1px;
    border-right: none;
  }
}


/**
 * Button input groups
 *
 * 1. Jankily prevent input button groups from wrapping with `white-space` and
      `font-size` in combination with `inline-block` on buttons.
 * 2. Negative margin for spacing, position for bringing hovered/focused/actived
      element above the siblings.
 * 3. Bring the "active" button to the front
 * 4. Negative margin to only have a 1px border between the two
 */

.k-input-group__button {
  position: relative;

  /* 1 */
  font-size: 0;
  white-space: nowrap;

  .k-button,
  .k-dropdown__menu {
    font-size: $font-size;
  }

  /* 2 */
  > .k-button {
    position: relative;
    font-size: $font-size;
    margin: 0;

    + .k-button {
      margin-left: -1px;
    }

    /* 3 */
    &:hover,
    &:focus,
    &:active {
      z-index: 2;
    }
  }

  /* 4 */
  &:first-child {
    > .k-button,
    > .k-button-group {
      margin-right: -1px;
    }

    @include rtl {
      > .k-button,
      > .k-button-group {
        margin-right: 0;
        margin-left: -1px;
      }
    }
  }

  &:last-child {
    > .k-button,
    > .k-button-group {
      z-index: 2;
      margin-left: -1px;
    }

    @include rtl {
      > .k-button,
      > .k-button-group {
        margin-left: 0;
        margin-right: -1px;
      }
    }
  }
}

/**
 * Designed to help with the organization and spacing of vertical forms. For
 * horizontal forms, use the predefined grid classes.
 */

.k-form-group {
  margin: 0;
  @include content-block;
}


/**
 * Form control sizing
 *
 * Build on `.k-form-control` with modifier classes to decrease or increase the
 * height and font-size of form controls.
 *
 * The `.k-form-group-* k-form-control` variations are sadly duplicated to avoid the
 * issue documented in https://github.com/twbs/bootstrap/issues/15074.
 */

.k-form-group--small {
  .k-form-control {
    font-size: $font-size-small;
    height: $input-height-small;
    padding: $spacing-small-vertical $spacing-small-horizontal;
    line-height: $line-height-small;
    border-radius: $border-radius-small;
  }

  select.k-form-control {
    height: $input-height-small;
    line-height: $input-height-small;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

.k-form-group--large {
  .k-form-control {
    font-size: $font-size-large;
    height: $input-height-large;
    padding: $spacing-large-vertical $spacing-large-horizontal;
    line-height: $line-height-large;
    border-radius: $border-radius-large;
  }

  select.k-form-control {
    height: $input-height-large;
    line-height: $input-height-large;
  }

  textarea.k-form-control,
  select[multiple].k-form-control {
    height: auto;
  }
}

.k-inline-form-group {
  display: inline-block;
  margin-right: 1em;

  &:last-child {
    margin-right: 0;
  }

  @include rtl {
    margin-right: 0;
    margin-left: 1em;

    &:last-child {
      margin-left: 0;
      margin-right: 1em;
    }
  }

  .k-input-group,
  .k-button-group {
    display: block;
  }
}

.k-form-row-group {
  background: $color-primary-plus-6;
  border-radius: 5px;
  border: 1px solid $color-primary-min-2;
  margin-bottom: $spacing;
}

.k-form-row {
  margin: 0;
  border-bottom: 1px solid $color-primary-min-2;
  padding-bottom: 10px;

  &:last-child {
    border-bottom: 0;
  }

  @include breakpoint($beta) {
    display: table;
    width: 100%;
    padding-bottom: 0;

    @include rtl {
      direction: rtl;
    }

    &:last-child {
      border-bottom: none;
    }
  }

  .k-form-row__item {
    padding: 10px 10px 0 10px;

    @include breakpoint($beta) {
      display: table-cell;
      vertical-align: middle;
      padding: 10px;
      border-right: 1px solid $color-primary-min-2;

      @include rtl {
        border-right: none;
        border-left: 1px solid $color-primary-min-2;
      }
    }

    &:last-child {
      border-right: none;

      @include rtl {
        border-left: none;
      }
    }
  }

  .k-form-row__item--button,
  .k-form-row__item--label {
    width: 1%;
    white-space: nowrap;
  }

  .k-form-row__item--label {
    border-right: none;
    padding-right: 0;

    @include rtl {
      border-left: none;
      padding-right: 10px;
      padding-left: 0;
    }
  }

  .k-form-row__item--input {
    @include breakpoint($beta) {
      padding: 5px;
    }
  }

  .k-form-control {
    margin: 0;
  }

  .k-form-row__message {
    a {
      display: block;
      padding: 10px;

      &:focus,
      &:hover {
        background: $color-primary-plus-1;
      }
    }
  }

  &:first-child {
    .k-form-row__message {
      a {
        border-radius: 5px 5px 0 0;
      }
    }
  }

  &:last-child {
    .k-form-row__message {
      a {
        border-radius: 0 0 5px 5px;
      }
    }
  }
}


/* Using import so we can include the file input on it's own in site.scss */

@import "styled-file-input";

.k-file-input {
  position: relative;
  display: inline-block;


  /* The original file input select */

  input {

    /**
     * 1. Style only when JS is enabled / addEventlistener is available
     *    We want to show it regularly when we don't have addEventListener for usability sake
     * 2. Hide the label by default and only show it when addEventListener is available (e.g. when file input is hidden)
     * 3. Display label when file input is hidden
     */

    @include modernizr('.k-eventlistener') {
      @include coverall($left: false);
      margin: 0;
      padding: 0;
      cursor: pointer;
      font-size: 99px;
      opacity: 0;
      filter: alpha(opacity=0);
    }

    + label {
      display: none; /* 2 */

      @include modernizr('.k-eventlistener') {
        display: inline-block; /* 3 */
      }
    }


    /* Styling the label, holding both the button and the selected files container */

    + label {
      max-width: 100%;
      font-size: $font-size;
      line-height: $line-height;
      font-weight: $regular;
      text-overflow: ellipsis;
      white-space: nowrap;
      cursor: pointer;
      overflow: hidden;
      vertical-align: middle;
      color: $color-accent;
      padding: 0;
      text-align: center;
    }


    /* Focus state */

    &:focus,
    &.has-focus {
      outline: none;

      & + label {
        outline: 1px dotted #000;
        outline: -webkit-focus-ring-color auto 5px;
      }
    }


    /* Hover / focus state */

    &:focus,
    &.has-focus,
    &.has-drop-focus,
    &:hover{
      cursor: pointer;

      & + label {
        .k-file-input__button {
          &, &:hover, &:focus {
            background-color: darken($color-accent, 10%);
          }
        }
      }
    }
  }


  /* The button */

  .k-file-input__button {
    display: block;
    background-color: $color-accent;
    color: $color-accent-type;
    pointer-events: none;
    vertical-align: middle;
    font-weight: $regular;
    text-align: center;
    touch-action: manipulation;
    cursor: pointer;
    background-image: none; /* 2 */
    border: 1px solid transparent;
    white-space: nowrap;
    box-shadow: none;
    text-shadow: none;
    user-select: none;
    margin-bottom: 0;
    border-radius: $border-radius-large;
    font-size: $font-size-large;
    line-height: $line-height-large;
    padding: $spacing-large-vertical $spacing-large-horizontal;
  }


  /* Selected files container */

  .k-file-input__files {
    width: 100%;
    height: $line-height;
    line-height: $line-height;
    display: inline-block;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    vertical-align: top;
    text-align: center;

    &:empty {
      display: none;
    }
  }
}

.k-form-info {
  margin-top: 0.75em;
  @include content-block(1em);
  color: $color-type-lighter;
}

.k-gallery-container {
  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
    overflow: auto;
  }
}

.k-gallery {
  padding: $grid-spacing / 2;
  overflow: auto;

  @include flexbox {
    display: flex;
    flex-direction: column;
    flex: 1 1 auto;
  }
}

.k-gallery__items {
  @include clearfix;

  @include flexbox {
    flex: 1 1 auto;
  }
}

.k-gallery__item {
  float: left;
  padding: $grid-spacing / 2;
  width: 100%;

  @include rtl {
    float: right;
  }


  /* Set breakpoints */

  @include setGridBreakpoint;


  /* Selected styling */

  &.k-is-selected {
    .k-card__header {
      background: $color-accent;
      border-color: darkest($color-accent);
      color: $color-accent-type;

      a {
        color: $color-accent-type;
      }
    }

    .k-card__body {
      color: $color-accent-type;
      border-color: darker($color-accent);
    }

    &:hover,
    &:focus {
      .k-card__header {
        background: darkest($color-accent);
      }
    }
  }
}


/* Make sure that the files are separated from the folders */

.k-gallery__item--folder + .k-gallery__item--file {
  clear: left;

  @include rtl {
    clear: right;
  }
}

.k-heading {
  font-size: $font-size-huge;
  line-height: $line-height-huge;
  @include content-block($spacing);
}

.k-icon--accent {
  color: $color-accent;
}

.k-icon--success {
  color: $color-success;
}

.k-icon--error {
  color: $color-error;
}

.k-icon--size-tiny {
